import React, { useContext, useEffect ,useState} from 'react'
import { useNavigate,Route } from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';

import Sidebar from "../dashbord/Sidebar";
import FitnessPage from "../dashbord/MyFitness";
import QRCodePage from "../dashbord/QRCode";
import ProfilePage from "../dashbord/Profile";

const Dashboard = () => {
  const { logindata, setLoginData } = useContext(LoginContext);
  const [data, setData] = useState(false);
  const history = useNavigate();

  const DashboardValid = async () => {
    let token = localStorage.getItem("usersdatatoken");

    const res = await fetch("/validuser", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: token,
      },
    });

    const data = await res.json();

    if (data.status == 401 || !data) {
      history("*");
    } else {
      console.log("user verify");
      setLoginData(data);
      history("/dashboard/fitness");
    }
  };

  useEffect(() => {
    setTimeout(() => {
      DashboardValid();
      setData(true);
    }, 2000);
  }, []);

  return (
    <div className="flex min-h-screen">
      <Sidebar />
      <div className="flex-grow bg-gray-100">
        {data ? (
          <>
            <Route path="/dashboard/fitness" component={FitnessPage} />
            <Route path="/dashboard/qr-code" component={QRCodePage} />
            <Route path="/dashboard/profile" component={ProfilePage} />
          </>
        ) : (
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              height: "100vh",
            }}
          >
            Loading... &nbsp;
            <CircularProgress />
          </Box>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
