import React, { useState, useEffect, useRef } from 'react';
import { FaTrashAlt, FaEdit, FaPlus, FaTimes } from 'react-icons/fa';
import PackageInfoPopup from './PackageInfoPopup';
import PaymentPopup from './PaymentPopup';

function PackageList() {
  const [packages, setPackages] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [showPaymentPopup, setShowPaymentPopup] = useState(false);
  const sliderRef = useRef(null);

  // Fetch packages data from the backend
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await fetch('/api/packages');
        const data = await response.json();
        setPackages(data.packages);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPackages();
  }, []);

  useEffect(() => {
    const sliderElement = sliderRef.current;
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowRight' && currentSlide < Math.ceil(packages.length / 6) - 1) {
        setCurrentSlide((prevSlide) => prevSlide + 1);
      } else if (event.key === 'ArrowLeft' && currentSlide > 0) {
        setCurrentSlide((prevSlide) => prevSlide - 1);
      }
    };

    sliderElement.addEventListener('keydown', handleKeyDown);

    return () => {
      sliderElement.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentSlide, packages.length]);

  const handlePrevSlide = () => {
    if (currentSlide > 0) {
      setCurrentSlide((prevSlide) => prevSlide - 1);
    }
  };

  const handleNextSlide = () => {
    if (currentSlide < Math.ceil(packages.length / 6) - 1) {
      setCurrentSlide((prevSlide) => prevSlide + 1);
    }
  };

  const handleOpenDescription = (pkg) => {
    setSelectedPackage(pkg);
  };

  const handleCloseDescription = () => {
    setSelectedPackage(null);
  };

  const handleBuyPackage = (pkg) => {
    setSelectedPackage(pkg);
    setShowPaymentPopup(true);
  };

  const handleClosePaymentPopup = () => {
    setShowPaymentPopup(false);
    setSelectedPackage(null); // Close the PackageInfoPopup as well
  };

  const startCardIndex = currentSlide * 6;
  const visiblePackages = packages.slice(startCardIndex, startCardIndex + 6);

  return (
    <div className="h-327px">
      <div
        className="container overflow-hidden"
        ref={sliderRef}
        tabIndex="0"
        style={{ outline: 'none' }}
      >
        <div
          className="grid grid-cols-3 gap-4 mt-2"
          style={{
            transform: `translateX(-${currentSlide * (100 / 3)}%)`,
            transition: 'transform 0.3s ease-in-out',
            gridTemplateRows: 'repeat(2, minmax(0, 1fr))',
          }}
        >
          {visiblePackages.map((pkg) => (
            <div
              key={pkg._id}
              className="relative border border-gray-300 rounded-lg p-4"
              style={{ height: '180px' }}
            >
              <h2 className="text-xl font-bold mb-4">{pkg.name}</h2>
              <p className="mb-2">{pkg.description}</p>
              <p className="text-lg font-bold">${pkg.price}</p>
              <div className="absolute bottom-0 right-0">
                <button
                  className="text-blue-500 hover:text-blue-700 mr-2"
                  onClick={() => handleOpenDescription(pkg)}
                >
                  <FaEdit />
                </button>
                <button
                  className="text-red-500 hover:text-red-700"
                  onClick={() => handleDeletePackage(pkg._id)}
                >
                  <FaTrashAlt />
                </button>
              </div>
            </div>
          ))}
          <div className="relative border border-gray-300 rounded-lg p-4 bg-gray-100 flex justify-center items-center">
            <button
              className="text-6xl text-gray-400"
              onClick={() => handleBuyPackage()}
              style={{ outline: 'none' }}
            >
              <FaPlus />
            </button>
          </div>
        </div>
      </div>
      <div className="flex items-center justify-center mt-4">
        <button
          className={`rounded-full bg-gray-300 p-2 mr-2 ${
            currentSlide === 0 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-400'
          }`}
          disabled={currentSlide === 0}
          onClick={handlePrevSlide}
        >
          <span className="sr-only">Previous</span>
          <FaTimes className="h-4 w-4" />
        </button>
        <button
          className={`rounded-full bg-gray-300 p-2 ${
            currentSlide === Math.ceil(packages.length / 6) - 1
              ? 'opacity-50 cursor-not-allowed'
              : 'hover:bg-gray-400'
          }`}
          disabled={currentSlide === Math.ceil(packages.length / 6) - 1}
          onClick={handleNextSlide}
        >
          <span className="sr-only">Next</span>
          <FaTimes className="h-4 w-4 transform rotate-45" />
        </button>
      </div>
      {selectedPackage && (
        <PackageInfoPopup pkg={selectedPackage} onClose={handleCloseDescription} />
      )}
      {showPaymentPopup && (
        <PaymentPopup pkg={selectedPackage} onClose={handleClosePaymentPopup} />
      )}
    </div>
  );
}

export default PackageList;
