import React, { useState } from 'react';
import { FaUser,FaTimes,FaUserPlus,FaUserEdit  } from 'react-icons/fa';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import Adduserdata from '../dashbord/profileitems/Adduserdata';


function Profile() {

  //calender
  const [date, setDate] = useState(new Date());
  const [showPopup, setShowPopup] = useState(false);

  const handleDateChange = (date) => {
    setDate(date);
  };

  const handlePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className='bg-white flex rounded-lg'>
      {/* First Part */}
      <div className='w-3/4 rounded-l-lg bg-white p-8 min-h-screen'>
        <div className='flex justify-center items-center mb-8'>
          <FaUser className='text-4xl mr-4' />
          <h1 className='text-4xl font-bold'>Profile Details</h1>
        </div>
        
        <div>

        </div>
       
      </div>
      {/* Second Part */}
      <div className='w-1/4 rounded-r-lg bg-gray-700 p-8'>
        <div className='flex flex-col justify-center items-center'>
          <Calendar value={date} onChange={handleDateChange} />
          <button className='bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-5 rounded focus:outline-none focus:shadow-outline inline-flex items-center mt-4 w-full justify-center' onClick={handlePopup}>  <FaUserPlus className='mr-2' />Add Data</button>
          {showPopup && (
            <div className='fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center'>
            <div className='bg-yellow-500 w-3/4 h-4/5 rounded-lg p-8 relative'>
              <button className='bg-red-500 text-white font-bold py-2 px-4 rounded absolute top-0 right-0 mt-2 mr-2' onClick={handleClosePopup}><FaTimes /></button>
              <Adduserdata/>
             
            </div>
          </div>
          
          )}
          <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-5 rounded focus:outline-none focus:shadow-outline inline-flex items-center w-full justify-center mt-4'><FaUserEdit className='mr-2' />Update Data</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
