import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
} from "@chakra-ui/react";
import { ChakraProvider } from "@chakra-ui/react";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./mix.css";

const Register = () => {
  const [formData, setFormData] = useState({
    fname: "",
    email: "",
    password: "",
    cpassword: ""
  });
  const history = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Form validation
    if (!formData.fname || !formData.email || !formData.password || !formData.cpassword) {
      return toast.error("Please fill in all fields");
    }

    if (formData.password !== formData.cpassword) {
      return toast.error("Password and Confirm Password do not match");
    }

    try {
      const response = await fetch("http://localhost:8009/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          ...formData,
          role: "user"
        })
      });

      if (!response.ok) {
        throw new Error("Failed to register");
      }

      
      toast.success("Registration successful!");
      history("/login"); 
    } catch (error) {
      console.error(error); 
      toast.error("Failed to register");
    }
  };


  const navlogin = ()=>{
    history("/login"); 

  }

  return (
    <ChakraProvider>
      <div className="form-background">
        <Box maxW="md" mx="auto" p="6">
          <div className="m-30 bg-gray-100 rounded-xl">
          <div className="form_heading">
                        <h1 className="text-black font-bold text-3xl p-5">Welcome Topjobs !</h1>
                        <p className="text-black font-bold p-2">Hi, Get started for free - Register today!</p>
                    </div>
            <Box p={4}>
              <form onSubmit={handleSubmit}>
                <FormControl id="fname" isRequired>
                  <FormLabel>Full Name</FormLabel>
                  <Input
                    type="text"
                    name="fname"
                    value={formData.fname}
                    onChange={handleChange}
                  />
                </FormControl>
                <FormControl id="email" isRequired mt={4}>
                  <FormLabel>Email</FormLabel>
                  <Input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </FormControl>
                <FormControl id="password" isRequired mt={4}>
                  <FormLabel>Password</FormLabel>
                  <Input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                </FormControl>
                <FormControl id="cpassword" isRequired mt={4}>
                  <FormLabel>Confirm Password</FormLabel>
                  <Input
                    type="password"
                    name="cpassword"
                    value={formData.cpassword}
                    onChange={handleChange}
                  />
                </FormControl>
                <Button type="submit" className="btn" mt={4}>
                  Register
                </Button>

              
              </form>
              <button class="mt-2 text-center hover:text-blue-700"  onClick={navlogin}> if you have already account, login now</button>
            </Box>
          </div>
        </Box>
      </div>
      <ToastContainer /> 
    </ChakraProvider>
  );
};

export default Register;
