import React, { useState } from "react";
import { loadStripe } from "@stripe/stripe-js";

const stripePromise = loadStripe("pk_test_51OviJeSCkJx3glD9ied9hu2ePHsjoU9Mt6FNwEHH1mv5OcKneWEFqNqZYHioWCwiSCZxnHBvi98qm01TYjPYotAr00ALHdsty4");

const BookAppointment = () => {
  const [step, setStep] = useState(1);
  const [appointmentData, setAppointmentData] = useState({
    date: "",
    location: "",
    description: ""
  });
  const [paymentData, setPaymentData] = useState({
    amount: "",
    token: null
  });

  const handleAppointmentSubmit = () => {
    setStep(2);
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="bg-indigo-100 p-8 rounded-lg">
        {step === 1 && (
          <form className="bg-gray-100 p-6 rounded-md space-y-4" onSubmit={handleAppointmentSubmit}>
            <h2 className="text-xl font-semibold">Book Appointment</h2>
            <div className="flex flex-col">
              <label htmlFor="date" className="mb-1">Date</label>
              <input
                type="date"
                id="date"
                value={appointmentData.date}
                onChange={(e) =>
                  setAppointmentData({ ...appointmentData, date: e.target.value })
                }
                className="border border-gray-300 p-2 rounded-md"
              />
            </div>
            <div className="flex flex-col">
              <label htmlFor="location" className="mb-1">Location</label>
              <input
                type="text"
                id="location"
                value={appointmentData.location}
                onChange={(e) =>
                  setAppointmentData({ ...appointmentData, location: e.target.value })
                }
                className="border border-gray-300 p-2 rounded-md"
              />
            </div>
            <div className="flex flex-col">
              <label htmlFor="description" className="mb-1">Description</label>
              <input
                type="text"
                id="description"
                value={appointmentData.description}
                onChange={(e) =>
                  setAppointmentData({ ...appointmentData, description: e.target.value })
                }
                className="border border-gray-300 p-2 rounded-md"
              />
            </div>
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md">Next</button>
          </form>
        )}
        {step === 2 && (
          <div className="bg-gray-100 p-6 rounded-md space-y-4">
            <h2 className="text-xl font-semibold">Payment</h2>
            <div className="flex flex-col">
              <label htmlFor="amount" className="mb-1">Amount</label>
              <input
                type="number"
                id="amount"
                value={paymentData.amount}
                onChange={(e) =>
                  setPaymentData({ ...paymentData, amount: e.target.value })
                }
                className="border border-gray-300 p-2 rounded-md"
              />
            </div>
            <button
              onClick={async () => {
                const stripe = await stripePromise;
                const { error } = await stripe.redirectToCheckout({
                  sessionId: paymentData.sessionId
                });
                if (error) {
                  console.error("Error:", error);
                }
              }}
              className="bg-blue-500 text-white px-4 py-2 rounded-md"
              role="link"
            >
              Pay Now
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookAppointment;
