import React, { useState } from 'react';
import { FaCartPlus, FaTimes } from 'react-icons/fa';

const FoodItemModal = ({ foodItem, onAddToCart, cartItems }) => {
  const [quantity, setQuantity] = useState(1);
  const [visible, setVisible] = useState(false);

  const itemInCart = cartItems.find((item) => item.foodItem._id === foodItem._id);

  const handleAddToCart = () => {
    onAddToCart(foodItem, quantity);
    setVisible(false);
  };

  return (
    <>
      <button
        className="mt-2 bg-orange-500 text-white px-4 py-2 rounded-md hover:bg-orange-600"
        onClick={() => setVisible(true)}
      >
        Add to Cart
      </button>
      {itemInCart && <p className="text-md font-medium text-gray-600 mb-2">In Cart: {itemInCart.quantity}</p>}
      {visible && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-4 rounded-lg w-96 relative">
            <div className="flex justify-end mb-2">
              <button className="text-gray-500 hover:text-gray-600" onClick={() => setVisible(false)}>
                <FaTimes size={20} />
              </button>
            </div>
            <img
              src={foodItem.photo}
              alt={foodItem.name}
              className="w-full h-64 object-cover rounded-md mb-4"
              style={{ objectFit: 'cover', maxHeight: '60%' }}
            />
            <h3 className="text-xl font-semibold text-gray-800 mb-2">{foodItem.name}</h3>
            <p className="text-lg font-medium text-gray-600 mb-2">Price: ${foodItem.price}</p>
            <p className="text-md font-medium text-gray-600 mb-2">
              Available: {foodItem.available ? 'Yes' : 'No'}
            </p>
            <p className="text-md font-medium text-gray-600 mb-2">Category: {foodItem.category}</p>
            <div className="flex items-center">
              <label htmlFor="quantity" className="mr-2">
                Quantity:
              </label>
              <input
                type="number"
                id="quantity"
                className="border rounded-md px-2 py-1"
                min="1"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
              />
              <button
                className="ml-4 bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600"
                onClick={handleAddToCart}
              >
                <FaCartPlus />
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FoodItemModal;
