import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const FoodItems = () => {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const fetchFoodItems = async () => {
    try {
      const response = await fetch('http://localhost:8009/food-items'); // Replace with your API URL
      const data = await response.json();
      setFoodItems(data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="bg-white min-h-screen p-2">
      <h1 className="text-2xl font-bold text-white mb-4 text-center rounded-xl p-2 bg-gradient-to-r from-green-300 via-blue-500 to-purple-600">
        Food Items
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {foodItems.map((foodItem) => (
          <Link key={foodItem.id} to={`/product/${foodItem.id}`} className="block">
            <div className="bg-gray-100 p-4 rounded-lg shadow-md hover:shadow-lg">
              <img src={foodItem.photo} alt={foodItem.name} className="w-full h-40 object-cover rounded" />
              <h2 className="text-xl font-semibold mt-2">{foodItem.name}</h2>
              <p className="text-lg font-bold mt-1">${foodItem.price.toFixed(2)}</p>
              <p className="text-sm mt-1">{foodItem.availability ? 'Available' : 'Out of Stock'}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default FoodItems;
