import React, { useState, useEffect } from "react";

const BMICalculator = () => {
  const [weight, setWeight] = useState("");
  const [height, setHeight] = useState("");
  const [bmi, setBMI] = useState(null);
  const [healthInfo, setHealthInfo] = useState("");
  const [userData, setUserData] = useState({});

  useEffect(() => {
    async function fetchUserData() {
        const response = await fetch(`/user/profile`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: localStorage.getItem('usersdatatoken'),
            },
          });
      const data = await response.json();
      setUserData(data);
    }
    fetchUserData();
  }, []);

  const calculateBMI = () => {
    const bmiValue = (weight / ((height/100) ** 2)).toFixed(1);
    setBMI(bmiValue);

    let healthInfoText = "";
    if (bmiValue < 18.5) {
      healthInfoText = "Underweight";
    } else if (bmiValue >= 18.5 && bmiValue < 25) {
      healthInfoText = "Normal weight";
    } else if (bmiValue >= 25 && bmiValue < 30) {
      healthInfoText = "Overweight";
    } else {
      healthInfoText = "Obese";
    }
    setHealthInfo(healthInfoText);
  };

  return (
    <div className="bg-white p-4 rounded-md">
      <h1 className="text-2xl font-bold mb-4 bg-blue-500 text-white rounded-md p-2">
        BMI Calculator
      </h1>
      <div className="flex flex-col mb-4">
        <label className="font-bold text-gray-500">Weight (in kg)</label>
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
          className="border border-gray-400 rounded-md px-3 py-2 mt-1"
        />
      </div>
      <div className="flex flex-col mb-4">
        <label className="font-bold text-gray-500">Height (in cm)</label>
        <input
          type="number"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
          className="border border-gray-400 rounded-md px-3 py-2 mt-1"
        />
      </div>
      <button
        onClick={calculateBMI}
        className="bg-blue-500 text-white rounded-md px-3 py-2"
      >
        Calculate BMI
      </button>
      {bmi && (
        <div className="mt-4">
          <p className="font-bold text-gray-500">Your BMI is: {bmi}</p>
          <p className="font-bold text-gray-500">Health information: {healthInfo}</p>
          <p className="font-bold text-gray-500">User Weight: {userData.weight}</p>
          <p className="font-bold text-gray-500">User Height: {userData.height}</p>
        </div>
      )}
    </div>
  );
};

export default BMICalculator;
