import { ClipLoader } from "react-spinners";
// import { FaInfoCircle } from "react-icons/fa";

const FoodItems = () => {
  const [userData, setUserData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null); // Add this line for error state

  // Food items state
  const [foodItems, setFoodItems] = useState([]);
  const [foodItemsLoading, setFoodItemsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Your existing code to fetch user data
        // ...

      } catch (error) {
        setError(error);
        setIsLoading(false);
      }
    };

    const fetchFoodItems = async () => {
      try {
        const response = await axios.get('http://localhost:8009/food-items');
        setFoodItems(response.data.foodItems);
        setFoodItemsLoading(false);
      } catch (error) {
        console.log(error);
        setFoodItemsLoading(false);
      }
    };

    fetchUserData();
    fetchFoodItems();
  }, []);

  // Your existing code for loading and error handling
  // ...

  return (
    <>
      {isLoading ? (
       <>
       </>
      ) : error ? (
       <>
       </>
      ) : (
        <div>
          {/* User details */}
          {/* Your existing code to display user details */}
          {/* ... */}

          {/* Food items */}
          <div className="grid grid-cols-3 gap-4 mt-4">
            {foodItemsLoading ? (
              // Food items loading spinner
              <ClipLoader size={50} color="#FFFFFF" loading={foodItemsLoading} />
            ) : (
              // Display food items as cards
              foodItems.map((foodItem) => (
                <div key={foodItem._id} className="bg-white p-4 rounded-md shadow-md">
                  <h2 className="text-xl font-bold mb-2">{foodItem.name}</h2>
                  <p className="text-gray-600 mb-2">Price: ${foodItem.price}</p>
                  <p className="text-gray-600 mb-4">Category: {foodItem.category}</p>
                  {/* Add more food item details as needed */}
                  {/* ... */}
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default FoodItems;
