import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const consultants = [
  {
    name: "John Doe",
    photo: "john-doe.jpg",
    country: "United States",
    availableTime: "Monday to Friday, 9am - 5pm",
  },
  {
    name: "Jane Smith",
    photo: "jane-smith.jpg",
    country: "United Kingdom",
    availableTime: "Tuesday to Saturday, 10am - 6pm",
  },
  // Add more consultant objects as needed
];

const ConsultantCarousel = () => {
  // Divide consultants into groups of four
  const consultantGroups = consultants.reduce((accumulator, consultant, index) => {
    const groupIndex = Math.floor(index / 4);
    if (!accumulator[groupIndex]) {
      accumulator[groupIndex] = [];
    }
    accumulator[groupIndex].push(consultant);
    return accumulator;
  }, []);

  return (
    <div>
      {consultantGroups.map((group, groupIndex) => (
        <Carousel key={groupIndex} showArrows={true} infiniteLoop={true} showThumbs={false}>
          {group.map((consultant, index) => (
            <div key={index} className="flex justify-center items-center">
              <div className="max-w-md mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
                <img className="w-full h-56 object-cover" src={consultant.photo} alt={consultant.name} />
                <div className="p-4">
                  <h2 className="text-gray-800 text-lg font-bold">{consultant.name}</h2>
                  <p className="text-gray-600 text-sm">{consultant.country}</p>
                  <p className="text-gray-600 text-sm">{consultant.availableTime}</p>
                  <button className="bg-blue-600 text-white hover:bg-blue-700 font-bold py-2 px-4 mt-2 rounded-full">
                    Book Now
                  </button>
                </div>
              </div>
            </div>
          ))}
        </Carousel>
      ))}
    </div>
  );
};

export default ConsultantCarousel;
