import React, { useState } from "react";

const UserBMIcal = () => {
  const [weight, setWeight] = useState("");
  const [height, setHeight] = useState("");
  const [bmi, setBMI] = useState("");

  const calculateBMI = (e) => {
    e.preventDefault();
    const bmiValue = (weight / Math.pow(height / 100, 2)).toFixed(1);
    setBMI(bmiValue);
  };

  return (
    <>
      <div className='bg-white p-4 rounded-md'>
        <h1 className='text-2xl font-bold mb-4 bg-gradient-to-r from-fuchsia-500 to-cyan-500 bg-clip-text text-transparent pl-1 rounded-lg'>
          BMI Calculator
        </h1>
        <form onSubmit={calculateBMI}>
          <div className="flex flex-col mb-4">
            <label htmlFor="weight" className="font-bold text-gray-500 mb-2">
              Weight (kg)
            </label>
            <input
              type="number"
              id="weight"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
              className="border py-2 px-3 text-grey-800"
              required
            />
          </div>
          <div className="flex flex-col mb-4">
            <label htmlFor="height" className="font-bold text-gray-500 mb-2">
              Height (cm)
            </label>
            <input
              type="number"
              id="height"
              value={height}
              onChange={(e) => setHeight(e.target.value)}
              className="border py-2 px-3 text-grey-800"
              required
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Calculate BMI
          </button>
        </form>
        {bmi && (
          <div className="mt-4">
            <p className="font-bold text-gray-500">Your BMI:</p>
            <p className="text-xl">{bmi}</p>
          </div>
        )}
      </div>
    </>
  );
};

export default UserBMIcal;
