import React, { useState } from 'react';
import { toast } from 'react-toastify';

function PackagePage() {
  const [selectedPackage, setSelectedPackage] = useState(null);

  const handleBuyClick = async (packageId) => {
    try {
      const response = await fetch('/api/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('usersdatatoken')}`,
        },
        body: JSON.stringify({ packageId }),
      });

      if (response.ok) {
        toast.success('Package purchased successfully');
        setSelectedPackage(null);
      } else {
        const data = await response.json();
        throw new Error(data.error);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  // Render the packages with buy buttons
  return (
    <div>
      {/* Render the packages */}
      <div>
        <h2>Package 1</h2>
        <button onClick={() => handleBuyClick('package1')}>Buy</button>
      </div>
      <div>
        <h2>Package 2</h2>
        <button onClick={() => handleBuyClick('package2')}>Buy</button>
      </div>
      {/* Add more packages as needed */}
    </div>
  );
}

export default PackagePage;
