import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  VStack,
  Text,
  useToast,
  useColorModeValue
} from "@chakra-ui/react";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51OviJeSCkJx3glD9ied9hu2ePHsjoU9Mt6FNwEHH1mv5OcKneWEFqNqZYHioWCwiSCZxnHBvi98qm01TYjPYotAr00ALHdsty4");

const BookAppointment = () => {
  const [appointmentData, setAppointmentData] = useState({
    date: "",
    location: "",
    description: ""
  });
  const [paymentData, setPaymentData] = useState({
    amount: "",
    paymentMethod: ""
  });
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const toast = useToast();
  const bgColor = useColorModeValue("gray.100", "gray.700");
  const stripe = useStripe();
  const elements = useElements();

  const handleAppointmentSubmit = () => {
    setShowPaymentForm(true);
  };

  const handlePaymentSubmit = async () => {
    try {
      if (!stripe || !elements) {
        // Stripe.js has not yet loaded.
        // Make sure to disable form submission until Stripe.js has loaded.
        return;
      }
      
      const cardElement = elements.getElement(CardElement);
      
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardElement
      });
      
      if (error) {
        throw new Error(error.message);
      }
      
      const response = await fetch("/payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ amount: paymentData.amount, paymentMethod: paymentMethod.id })
      });
      
      const paymentIntent = await response.json();
      
      const result = await stripe.confirmCardPayment(paymentIntent.client_secret, {
        payment_method: paymentMethod.id
      });
      
      if (result.error) {
        throw new Error(result.error.message);
      }
      
      // Payment successful
      toast({
        title: "Success",
        description: "Payment processed successfully",
        status: "success",
        duration: 5000,
        isClosable: true
      });
    } catch (error) {
      console.error(error);
      toast({
        title: "Error",
        description: "Failed to process payment",
        status: "error",
        duration: 5000,
        isClosable: true
      });
    }
  };

  return (
    <Box p={4}>
      <VStack spacing={8}>
        <Box bg={bgColor} p={4} borderRadius="md">
          <Text fontWeight="bold">Appointment Details</Text>
          <FormControl>
            <FormLabel>Date</FormLabel>
            <Input
              type="date"
              value={appointmentData.date}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, date: e.target.value })
              }
            />
          </FormControl>
          <FormControl>
            <FormLabel>Location</FormLabel>
            <Input
              type="text"
              value={appointmentData.location}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, location: e.target.value })
              }
            />
          </FormControl>
          <FormControl>
            <FormLabel>Description</FormLabel>
            <Input
              type="text"
              value={appointmentData.description}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, description: e.target.value })
              }
            />
          </FormControl>
          {!showPaymentForm && (
            <Button onClick={handleAppointmentSubmit}>Proceed to Payment</Button>
          )}
        </Box>
        {showPaymentForm && (
          <Box bg={bgColor} p={4} borderRadius="md">
            <Text fontWeight="bold">Payment Details</Text>
            <FormControl>
              <FormLabel>Amount</FormLabel>
              <Input
                type="number"
                value={paymentData.amount}
                onChange={(e) =>
                  setPaymentData({ ...paymentData, amount: e.target.value })
                }
              />
            </FormControl>
            <CardElement />
            <Button onClick={handlePaymentSubmit}>Pay Now</Button>
          </Box>
        )}
      </VStack>
    </Box>
  );
};

export default BookAppointment;
