import React, { useContext } from 'react';
import Avatar from '@mui/material/Avatar';
import "./header.css";
import weblogo from '../assests/home/logo.jpeg';
import { LoginContext } from './ContextProvider/Context';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { useNavigate, NavLink } from "react-router-dom";


const Header = () => {
  const { logindata, setLoginData } = useContext(LoginContext);
  const history = useNavigate();

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const logoutuser = async () => {
    let token = localStorage.getItem("usersdatatoken");

    const res = await fetch("/logout", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
        Accept: "application/json"
      },
      credentials: "include"
    });

    const data = await res.json();
    console.log(data);

    if (data.status == 201) {
      console.log("use logout");
      // alert("user logout");
      // Display alert message
    const alertDiv = document.createElement('div');
    alertDiv.innerHTML = 'User logged out';
    alertDiv.classList.add('bg-red-500', 'text-white', 'p-4', 'mt-4', 'rounded');
    document.body.appendChild(alertDiv);
      localStorage.removeItem("usersdatatoken");
      setLoginData(false)
      history("/");
    } else {
      console.log("error");
    }
  }

  const goDash = () => {
    history("/dash")
  }

  const goError = () => {
    history("*")
  }

  return (
    <>
      <header>
        <nav>
        <div className="logo-container">
            <NavLink to="/">
            <img src={weblogo} alt="HP Cloud Logo"/>
            </NavLink>
          </div>
          <div className="nav-buttons">
            <NavLink to="/">Home</NavLink>
            <NavLink to="/Services">Service</NavLink>
            <NavLink to="/contact">Contact</NavLink>
            {logindata.ValidUserOne ?
              <>
                <Avatar style={{ background: "salmon", fontWeight: "bold", textTransform: "capitalize" }} onClick={handleClick}>{logindata.ValidUserOne.fname[0].toUpperCase()}</Avatar>
                <Menu
                  id="basic-menu"
                  anchorEl={anchorEl}
                  open={open}
                  onClose={handleClose}
                  MenuListProps={{
                    'aria-labelledby': 'basic-button',
                  }}
                >
                  <MenuItem onClick={() => {
                    goDash()
                    handleClose()
                  }}>Profile</MenuItem>
                  <MenuItem onClick={() => {
                    logoutuser()
                    handleClose()
                  }}>Logout</MenuItem>
                </Menu>
              </>
              :
              <>
                <div className='logbtn'><NavLink to="/login">Login</NavLink></div>
                <div className='regbtn'> <NavLink to="/register">Register</NavLink></div>
              </>
            }
          </div>
        </nav>
      </header>
    </>
  )
}

export default Header;
