import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Grid,
  GridItem,
  Input,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
} from "@chakra-ui/react";
import { ChakraProvider } from "@chakra-ui/react";

const Register = () => {
  const [formData, setFormData] = useState({
    fname: "",
    email: "",
    password: "",
    cpassword: "",
    role: "",
    firstName: "",
    lastName: "",
    designation: "",
    education: "",
    address: "",
    dob: "",
    nic: "",
    telephoneNumber: "",
    available: "",
    nationality: "",
    consultantType: "",
    availableTime: "",
    description: "",
    photo: null,
    cv: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    for (let key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      const response = await fetch("http://localhost:8009/register", {
        method: "POST",
        body: formDataToSend,
      });

      if (!response.ok) {
        throw new Error("Failed to register");
      }

      const responseData = await response.json();
      console.log("Registration successful:", responseData);
    } catch (error) {
      console.error("Error registering:", error.message);
    }
  };

  return (
    <ChakraProvider>
      <div className="bg-blue-200">
        <Box maxW="md" mx="auto" p="6">
          <div className="m-30 bg-gray-100 rounded-xl">
            <Tabs isFitted variant="enclosed">
              {" "}
              {/* Adjust the width as needed */}
              <TabList mb="1em">
                <Tab className="bg-red-500">Job Seeker</Tab>
                <Tab>Consult</Tab>
              </TabList>
              <TabPanels>
                <TabPanel>
                  <form onSubmit={handleSubmit}>
                    <Grid templateColumns="repeat(2, 1fr)" gap={4}>
                      <GridItem colSpan={1}>
                        <FormControl isRequired>
                          <FormLabel>User Name</FormLabel>
                          <Input
                            name="fname"
                            value={formData.fname}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Email</FormLabel>
                          <Input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Password</FormLabel>
                          <Input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Confirm Password</FormLabel>
                          <Input
                            type="password"
                            name="cpassword"
                            value={formData.cpassword}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Role</FormLabel>
                          <Input
                            type="password"
                            name="cpassword"
                            value={formData.cpassword}
                            onChange={handleChange}
                          />
                        </FormControl>
                        {/* Add more basic details form fields */}
                      </GridItem>
                      <GridItem colSpan={1}>
                        <FormControl isRequired>
                          <FormLabel>Last Name</FormLabel>
                          <Input
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Designation</FormLabel>
                          <Input
                            name="designation"
                            value={formData.designation}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Education</FormLabel>
                          <Input
                            name="education"
                            value={formData.education}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Address</FormLabel>
                          <Input
                            name="address"
                            value={formData.address}
                            onChange={handleChange}
                          />
                        </FormControl>
                        {/* Add more additional details form fields */}
                      </GridItem>
                    </Grid>
                    <FormControl>
                      <FormLabel>Upload Photo</FormLabel>
                      <Input
                        type="file"
                        name="photo"
                        onChange={handleFileChange}
                      />
                    </FormControl>
                    <FormControl>
                      <FormLabel>Upload CV</FormLabel>
                      <Input
                        type="file"
                        name="cv"
                        onChange={handleFileChange}
                      />
                    </FormControl>
                    <Button type="submit" colorScheme="blue">
                      Register
                    </Button>
                  </form>
                </TabPanel>
                <TabPanel>
                <form onSubmit={handleSubmit}>
                    <Grid templateColumns="repeat(2, 1fr)" gap={4}>
                      <GridItem colSpan={1}>
                        <FormControl isRequired>
                          <FormLabel>User Name</FormLabel>
                          <Input
                            name="fname"
                            value={formData.fname}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Email</FormLabel>
                          <Input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Password</FormLabel>
                          <Input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Confirm Password</FormLabel>
                          <Input
                            type="password"
                            name="cpassword"
                            value={formData.cpassword}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Role</FormLabel>
                          <Input
                            type="password"
                            name="cpassword"
                            value={formData.cpassword}
                            onChange={handleChange}
                          />
                        </FormControl>
                        {/* Add more basic details form fields */}
                      </GridItem>
                      <GridItem colSpan={1}>
                        <FormControl isRequired>
                          <FormLabel>Last Name</FormLabel>
                          <Input
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Designation</FormLabel>
                          <Input
                            name="designation"
                            value={formData.designation}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Education</FormLabel>
                          <Input
                            name="education"
                            value={formData.education}
                            onChange={handleChange}
                          />
                        </FormControl>
                        <FormControl isRequired>
                          <FormLabel>Address</FormLabel>
                          <Input
                            name="address"
                            value={formData.address}
                            onChange={handleChange}
                          />
                        </FormControl>
                        {/* Add more additional details form fields */}
                      </GridItem>
                    </Grid>
                    <FormControl>
                      <FormLabel>Upload Photo</FormLabel>
                      <Input
                        type="file"
                        name="photo"
                        onChange={handleFileChange}
                      />
                    </FormControl>
                    <FormControl>
                      <FormLabel>Upload CV</FormLabel>
                      <Input
                        type="file"
                        name="cv"
                        onChange={handleFileChange}
                      />
                    </FormControl>
                    <Button type="submit" colorScheme="blue">
                      Register
                    </Button>
                  </form>
                </TabPanel>
              </TabPanels>
            </Tabs>
          </div>
        </Box>
      </div>
    </ChakraProvider>
  );
};

export default Register;
