import React, { useState } from 'react';
import { FaUserCircle } from 'react-icons/fa';

function Profile() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('/user-info', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ name, age, gender }),
      });

      const data = await response.json();

      if (response.ok) {
        // Update the user info in the UI
        setName(data.userInfo.name);
        setAge(data.userInfo.age);
        setGender(data.userInfo.gender);
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className='bg-gray-500 min-h-screen flex items-center justify-center'>
      <div className="max-w-sm mx-auto bg-white shadow-md rounded-md overflow-hidden">
        <div className="flex justify-center pt-8">
          <FaUserCircle className="text-6xl text-gray-500" />
        </div>
        <div className="p-6">
          <h1 className="text-2xl font-bold mb-2">{name}</h1>
          <p className="text-gray-700 mb-2">Age: {age}</p>
          <p className="text-gray-700 mb-4">Gender: {gender}</p>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="name" className="block font-bold text-gray-700 mb-2">
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full p-2 border rounded-md"
                value={name}
                onChange={(event) => setName(event.target.value)}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="age" className="block font-bold text-gray-700 mb-2">
                Age
              </label>
              <input
                type="number"
                id="age"
                name="age"
                className="w-full p-2 border rounded-md"
                value={age}
                onChange={(event) => setAge(event.target.value)}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="gender" className="block font-bold text-gray-700 mb-2">
                Gender
              </label>
              <select
                id="gender"
                name="gender"
                className="w-full p-2 border rounded-md"
                value={gender}
                onChange={(event) => setGender(event.target.value)}
              >
                <option value="">Select your gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div className="flex justify-end">
              <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                Save Changes
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}




export default Profile;
