import React, { useState } from 'react';
import { FaUserCircle } from 'react-icons/fa';

function Profile() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('/user-info', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ name, age, gender }),
      });

      const data = await response.json();

      if (response.ok) {
        // Update the user info in the UI
        setName(data.userInfo.name);
        setAge(data.userInfo.age);
        setGender(data.userInfo.gender);
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className='bg-gray-400  min-h-screen'>
      <div className="max-w-lg mt-4 mx-auto bg-white rounded-xl overflow-hidden shadow-md">
        <div className="bg-gray-100 px-6 py-4">
          <div className="w-32 h-32 bg-gray-300 rounded-full flex items-center justify-center mb-5">
            <FaUserCircle className="text-6xl text-gray-500" />
          </div>
          <h1 className="text-2xl font-bold mb-2">{name}</h1>
          <p className="text-gray-700">Location</p>
          <p className="text-gray-700">Bio</p>
        </div>
        <form onSubmit={handleSubmit} className="bg-white px-6 py-4">
          <label htmlFor="name" className="block mb-2 font-bold text-gray-700">
            Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            className="w-full p-2 border rounded-md"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
          <label htmlFor="age" className="block mt-3 mb-2 font-bold text-gray-700">
            Age
          </label>
          <input
            type="number"
            id="age"
            name="age"
            className="w-full p-2 border rounded-md"
            value={age}
            onChange={(event) => setAge(event.target.value)}
          />
          <label htmlFor="gender" className="block mt-3 mb-2 font-bold text-gray-700">
            Gender
          </label>
          <select
            id="gender"
            name="gender"
            className="w-full p-2 border rounded-md"
            value={gender}
            onChange={(event) => setGender(event.target.value)}
          >
            <option value="">Select your gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
          <button type="submit" className="mt-5 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Save Changes
          </button>
        </form>
      </div>
    </div>
  );
}

export default Profile;
