import React from 'react';

const CheckoutPage = ({ cartItems }) => {
  // Calculate the total price of cart items manually
  let totalPrice = 0;
  for (const item of cartItems) {
    totalPrice += item.foodItem.price * item.quantity;
  }

  return (
    <div className="container mx-auto mt-10 px-4">
      <h1 className="text-3xl font-bold mb-4">Checkout</h1>
      <div className="bg-white rounded-lg p-4 shadow-md">
        {cartItems.map((item) => (
          <div key={item.foodItem._id} className="flex items-center justify-between mb-2">
            <div>
              <h3 className="text-lg font-semibold">{item.foodItem.name}</h3>
              <p className="text-md text-gray-600">Quantity: {item.quantity}</p>
              <p className="text-md text-gray-600">Price: ${item.foodItem.price * item.quantity}</p>
            </div>
            {/* Add a button to remove items from the cart */}
            <button
              className="text-red-500 font-semibold"
              onClick={() => {
                // Implement remove item logic here
              }}
            >
              Remove
            </button>
          </div>
        ))}
        <hr className="my-4" />
        <div className="flex items-center justify-between">
          <h3 className="text-xl font-semibold">Total Price:</h3>
          <p className="text-xl font-semibold">${totalPrice}</p>
        </div>
        <button className="mt-4 bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600">
          Place Order
        </button>
      </div>
    </div>
  );
};

export default CheckoutPage;
