import React, { useState, useEffect, useRef } from 'react';
import { FaTrashAlt, FaEdit, FaPlus, FaTimes } from 'react-icons/fa';

function PackageList() {
  const [packages, setPackages] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [paymentData, setPaymentData] = useState({
    amount: '',
    startDate: '',
    endDate: '',
  });
  const sliderRef = useRef(null);

  // Fetch packages data from the backend
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await fetch('/api/packages');
        const data = await response.json();
        setPackages(data.packages);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPackages();
  }, []);

  useEffect(() => {
    const sliderElement = sliderRef.current;
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowRight' && currentSlide < Math.ceil(packages.length / 6) - 1) {
        setCurrentSlide((prevSlide) => prevSlide + 1);
      } else if (event.key === 'ArrowLeft' && currentSlide > 0) {
        setCurrentSlide((prevSlide) => prevSlide - 1);
      }
    };

    sliderElement.addEventListener('keydown', handleKeyDown);

    return () => {
      sliderElement.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentSlide, packages.length]);

  const handlePrevSlide = () => {
    if (currentSlide > 0) {
      setCurrentSlide((prevSlide) => prevSlide - 1);
    }
  };

  const handleNextSlide = () => {
    if (currentSlide < Math.ceil(packages.length / 6) - 1) {
      setCurrentSlide((prevSlide) => prevSlide + 1);
    }
  };

  const handleOpenDescription = (pkg) => {
    setSelectedPackage(pkg);
    const startDate = new Date();
    const endDate = new Date(startDate.getFullYear(), startDate.getMonth() + 1, startDate.getDate());
    setPaymentData({ amount: pkg.price, startDate, endDate });
  };

  const handleCloseDescription = () => {
    setSelectedPackage(null);
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/subscriptions/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          packageId: selectedPackage._id,
          startDate: paymentData.startDate,
          endDate: paymentData.endDate,
        }),
      });

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const startCardIndex = currentSlide * 6;
  const visiblePackages = packages.slice(startCardIndex, startCardIndex + 6);

  return (
    <div className="h-327px">
      <div
        className="container overflow-hidden"
        ref={sliderRef}
        tabIndex="0"
        style={{ outline: 'none' }}
      >
        <div
          className="grid grid-cols-3 gap-4 mt-2"
          style={{
            transform: `translateX(-${currentSlide * (100 / 3)}%)`,
            transition: 'transform 0.3s ease-in-out',
            gridTemplateRows: 'repeat(2, minmax(0, 1fr))',
          }}
        >
          {visiblePackages.map((pkg) => (
            <div
              key={pkg._id}
              className="bg-gray-200 p-4 rounded-md transition-transform duration-300 transform hover:scale-105 relative"
            >
              <h3 className="font-bold">{pkg.name}</h3>
              <p className="mt-2 text-gray-600">${pkg.price}</p>
              <div className="flex justify-between items-center mt-4">
                <div>
                  <button
                    className="mr-2 px-3 py-1 rounded-lg bg-red-600 text-white"
                    title="Delete"
                    onClick={() => handleOpenDescription(pkg)}
                  >
                    Buy
                  </button>
                </div>
                <div>
                  <button
                    className="text-gray-600 hover:text-gray-800 flex"
                    onClick={() => handleOpenDescription(pkg)}
                    title="Show Description"
                  >
                    More Details<FaPlus className="ml-2" />
                  </button>
                </div>
              </div>
              {selectedPackage && selectedPackage._id === pkg._id && (
                <div className="absolute inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75">
                  <div className="bg-white px-20 py-20 rounded-lg">
                    <h3 className="font-bold">Package: {selectedPackage.name}</h3>
                    <p className="mt-2 text-gray-600">Price: ${selectedPackage.price}</p>
                    <form onSubmit={handlePaymentSubmit}>
                      <div className="mt-2">
                        <label className="block">Amount</label>
                        <input
                          type="number"
                          className="w-full rounded-lg border-gray-300"
                          value={paymentData.amount}
                          disabled
                        />
                      </div>
                      <div className="mt-2">
                        <label className="block">Start Date</label>
                        <input
                          type="text"
                          className="w-full rounded-lg border-gray-300"
                          value={paymentData.startDate.toLocaleDateString()}
                          disabled
                        />
                      </div>
                      <div className="mt-2">
                        <label className="block">End Date</label>
                        <input
                          type="text"
                          className="w-full rounded-lg border-gray-300"
                          value={paymentData.endDate.toLocaleDateString()}
                          disabled
                        />
                      </div>
                      <div className="flex justify-center mt-4">
                        <button
                          className="mr-2 px-3 py-1 rounded-lg bg-red-600 text-white"
                          onClick={handleCloseDescription}
                          title="Close"
                        >
                          <FaTimes />
                        </button>
                        <button
                          type="submit"
                          className="px-3 py-1 rounded-lg bg-blue-500 text-white"
                          title="Subscribe"
                        >
                          Subscribe
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
      <div className="flex justify-center mt-4">
        <button
          className={`mr-2 px-3 py-1 rounded-lg ${
            currentSlide === 0 ? 'opacity-50 cursor-not-allowed' : 'bg-blue-500 text-white'
          }`}
          title="Previous Slide"
          onClick={handlePrevSlide}
          disabled={currentSlide === 0}
        >
          Prev
        </button>
        <button
          className={`mr-2 px-3 py-1 rounded-lg ${
            currentSlide >= Math.ceil(packages.length / 6) - 1
              ? 'opacity-50 cursor-not-allowed'
              : 'bg-blue-500 text-white'
          }`}
          title="Next Slide"
          onClick={handleNextSlide}
          disabled={currentSlide >= Math.ceil(packages.length / 6) - 1}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default PackageList;
