import React, { useState } from 'react';

function Profile() {
  const [bio, setBio] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');

  const handleAddProfile = (event) => {
    event.preventDefault();
    const token = localStorage.getItem('token');

    fetch('/api/user/profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ bio, location, skills })
    })
    .then(response => {
      if (response.ok) {
        console.log('Profile created successfully');
        setBio('');
        setLocation('');
        setSkills('');
      } else {
        throw new Error('Failed to create profile');
      }
    })
    .catch(error => {
      console.log(error.message);
    });
  };

  return (
    <div className='bg-gray-300 min-h-screen'>
      <h1>Profile</h1>
      <form onSubmit={handleAddProfile}>
        <div>
          <label htmlFor='bio'>Bio:</label>
          <textarea id='bio' name='bio' value={bio} onChange={e => setBio(e.target.value)} />
        </div>
        <div>
          <label htmlFor='location'>Location:</label>
          <input id='location' type='text' name='location' value={location} onChange={e => setLocation(e.target.value)} />
        </div>
        <div>
          <label htmlFor='skills'>Skills:</label>
          <input id='skills' type='text' name='skills' value={skills} onChange={e => setSkills(e.target.value)} />
        </div>
        <button type='submit'>Add Profile</button>
      </form>
    </div>
  );
}

export default Profile;
