import React, { useState } from 'react'
import { NavLink } from "react-router-dom"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./mix.css"

const Register = () => {
  const [currentStep, setCurrentStep] = useState(1); // Manage form steps

    const [passShow, setPassShow] = useState(false);
    const [cpassShow, setCPassShow] = useState(false);
    

    const [inpval, setInpval] = useState({
        fname: "",
        email: "",
        password: "",
        cpassword: ""
    });


    const setVal = (e) => {
        // console.log(e.target.value);
        const { name, value } = e.target;

        setInpval(() => {
            return {
                ...inpval,
                [name]: value
            }
        })
    };

    const addUserdata = async (e) => {
        e.preventDefault();

        const { fname, email, password, cpassword } = inpval;

        if (fname === "") {
            toast.warning("fname is required!", {
                position: "top-center"
            });
        } else if (email === "") {
            toast.error("email is required!", {
                position: "top-center"
            });
        } else if (!email.includes("@")) {
            toast.warning("includes @ in your email!", {
                position: "top-center"
            });
        } else if (password === "") {
            toast.error("password is required!", {
                position: "top-center"
            });
        } else if (password.length < 6) {
            toast.error("password must be 6 char!", {
                position: "top-center"
            });
        } else if (cpassword === "") {
            toast.error("cpassword is required!", {
                position: "top-center"
            });
        }
        else if (cpassword.length < 6) {
            toast.error("confirm password must be 6 char!", {
                position: "top-center"
            });
        } else if (password !== cpassword) {
            toast.error("pass and Cpass are not matching!", {
                position: "top-center"
            });
        } else {
            // console.log("user registration succesfully done");


            const data = await fetch("/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({
                    fname, email, password, cpassword
                })
            });

            const res = await data.json();
            setCurrentStep(2);
            // console.log(res.status);

            if (res.status === 201) {
                // setCurrentStep(2);
                // toast.success("Registration Successfully done ðŸ˜ƒ!", {
                //     position: "top-center"
                // });
                setInpval({ ...inpval, fname: "", email: "", password: "", cpassword: "" });
            }
        }
    }
  
    const [fullName, setFullName] = useState('');
    const [address, setAddress] = useState('');
    const [dob, setDob] = useState('');
    const [nic, setNic] = useState('');
    const [telephone, setTelephone] = useState('');
    
    const [weight, setWeight] = useState('');
    const [height, setHeight] = useState('');
    const [availability, setAvailability] = useState('');
    const [nationality, setNationality] = useState('');
    const [consultantType, setConsultantType] = useState('');
    const [availableTime, setAvailableTime] = useState('');
    const [description, setDescription] = useState('');
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8009/user/profile', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    fullName,
                    address,
                    dob,
                    nic,
                    telephone,
                    weight,
                    height,
                    availability,
                    nationality,
                    consultantType,
                    availableTime,
                    description
                })
            });
            
            const data = await response.json();
            console.log(data);
            
        } catch (error) {
            console.log(error);
        }
    };
    
  
  return (
    <>
      {currentStep === 1 && (
        <>
            <section className='form-background'>
                <div className="form_data">
                    <div className="form_heading">
                        <h1>Sign Up</h1>
                        <p style={{ textAlign: "center",margin:"5" }}>Globalize Your Career: Expert Consultation Tailored to Your Ambitions!</p>
                    </div>

                    <form>
                        <div className="form_input">
                            <label htmlFor="fname">Name</label>
                            <input type="text" onChange={setVal} value={inpval.fname} name="fname" id="fname" placeholder='Enter Your Name' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="email">Email</label>
                            <input type="email" onChange={setVal} value={inpval.email} name="email" id="email" placeholder='Enter Your Email Address' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="password">Password</label>
                            <div className="two">
                                <input type={!passShow ? "password" : "text"} value={inpval.password} onChange={setVal} name="password" id="password" placeholder='Enter Your password' />
                                <div className="showpass" onClick={() => setPassShow(!passShow)}>
                                    {!passShow ? "Show" : "Hide"}
                                </div>
                            </div>
                        </div>

                        <div className="form_input">
                            <label htmlFor="password">Confirm Password</label>
                            <div className="two">
                                <input type={!cpassShow ? "password" : "text"} value={inpval.cpassword} onChange={setVal} name="cpassword" id="cpassword" placeholder='Confirm password' />
                                <div className="showpass" onClick={() => setCPassShow(!cpassShow)}>
                                    {!cpassShow ? "Show" : "Hide"}
                                </div>
                            </div>
                        </div>

                        <button className='btn' onClick={addUserdata}>Next</button>
                        <p>Already have an account? <NavLink to="/">Log In</NavLink></p>
                    </form>
                    <ToastContainer />
                </div>
            </section>
        </>
      )}
      {currentStep === 2 && (

           //    <div className="form-background ">
    //    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-10">
        <form onSubmit={handleSubmit} className='form-background'>
           <div className='bg-white shadow-md rounded px-8 pt-6 pb-8 m-10'>
           <input type="text" placeholder="Full Name" value={fullName} onChange={(e) => setFullName(e.target.value)} />
        <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} />
        <input type="date" placeholder="Date of Birth" value={dob} onChange={(e) => setDob(e.target.value)} />
        <input type="text" placeholder="NIC" value={nic} onChange={(e) => setNic(e.target.value)} />
        <input type="tel" placeholder="Telephone" value={telephone} onChange={(e) => setTelephone(e.target.value)} />
        <input type="number" placeholder="Weight" value={weight} onChange={(e) => setWeight(e.target.value)} />
        <input type="number" placeholder="Height" value={height} onChange={(e) => setHeight(e.target.value)} />
        <input type="text" placeholder="Availability" value={availability} onChange={(e) => setAvailability(e.target.value)} />
        <input type="text" placeholder="Nationality" value={nationality} onChange={(e) => setNationality(e.target.value)} />
        <input type="text" placeholder="Consultant Type" value={consultantType} onChange={(e) => setConsultantType(e.target.value)} />
        <input type="text" placeholder="Available Time" value={availableTime} onChange={(e) => setAvailableTime(e.target.value)} />
        <input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
        <button type="submit">Submit</button>
            </div> 
        
      </form>
      )}
      <ToastContainer />
    </>
  );
};

export default Register;
