// export default Services;



import React, { useState, useEffect } from 'react';

const Services = () => {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const fetchFoodItems = async () => {
    try {
      const response = await fetch('http://localhost:8009/food-items');
      if (!response.ok) {
        throw new Error('Failed to fetch food items.');
      }
      const data = await response.json();
      setFoodItems(data.foodItems);
    } catch (error) {
      console.log('Error fetching food items:', error);
    }
  };

  return (
    <div className="food-items-page">
      <h1>Food Ordering System</h1>
      <div className="food-cards-container">
        {foodItems.map((foodItem) => (
          <div key={foodItem._id} className="food-card">
            <img src={foodItem.photo} alt={foodItem.name} />
            <h3>{foodItem.name}</h3>
            <p>Price: ${foodItem.price}</p>
            <p>Available: {foodItem.available ? 'Yes' : 'No'}</p>
            <p>Category: {foodItem.category}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
