import React, { useState } from 'react';
import { Box, Button, FormControl, FormLabel, Input, VStack } from '@chakra-ui/react';
import { ChakraProvider } from '@chakra-ui/react';

const Register = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        cpassword: '',
        role: '',
        designation: '',
        education: '',
        address: '',
        dob: '',
        nic: '',
        telephoneNumber: '',
        available: '',
        nationality: '',
        consultantType: '',
        availableTime: '',
        description: '',
        photo: null,
        cv: null
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setFormData({ ...formData, [name]: files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (let key in formData) {
            formDataToSend.append(key, formData[key]);
        }
        // Send formDataToSend to backend using fetch or axios
        console.log('Form submitted:', formDataToSend);
    };

    return (
        <Box maxW="md" mx="auto" p="6">
            <form onSubmit={handleSubmit}>
                <VStack spacing="4">
                    <FormControl isRequired>
                        <FormLabel>First Name</FormLabel>
                        <Input name="firstName" value={formData.firstName} onChange={handleChange} />
                    </FormControl>
                    <FormControl isRequired>
                        <FormLabel>Last Name</FormLabel>
                        <Input name="lastName" value={formData.lastName} onChange={handleChange} />
                    </FormControl>
                    <FormControl isRequired>
                        <FormLabel>Email</FormLabel>
                        <Input type="email" name="email" value={formData.email} onChange={handleChange} />
                    </FormControl>
                    <FormControl isRequired>
                        <FormLabel>Password</FormLabel>
                        <Input type="password" name="password" value={formData.password} onChange={handleChange} />
                    </FormControl>
                    <FormControl isRequired>
                        <FormLabel>Confirm Password</FormLabel>
                        <Input type="password" name="cpassword" value={formData.cpassword} onChange={handleChange} />
                    </FormControl>
                    {/* Add more form fields for basic details */}
                    <FormControl>
                        <FormLabel>Upload Photo</FormLabel>
                        <Input type="file" name="photo" onChange={handleFileChange} />
                    </FormControl>
                    <FormControl>
                        <FormLabel>Upload CV</FormLabel>
                        <Input type="file" name="cv" onChange={handleFileChange} />
                    </FormControl>
                    <Button type="submit" colorScheme="blue">Register</Button>
                </VStack>
            </form>
        </Box>
    );
};

export default Register;
