import React, { useState, useEffect } from 'react';
import { FaCartPlus, FaTimes } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import FoodItemModal from '../../dashbord/food/FoodItem';

const Services = () => {
  const [foodItems, setFoodItems] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceFilter, setPriceFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [cartItems, setCartItems] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  const itemsPerPage = 8;

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const fetchFoodItems = async () => {
    try {
      const response = await fetch('http://localhost:8009/food-items');
      if (!response.ok) {
        throw new Error('Failed to fetch food items.');
      }
      const data = await response.json();
      setFoodItems(data.foodItems);
    } catch (error) {
      console.log('Error fetching food items:', error);
    }
  };

  const filteredFoodItems = foodItems.filter((foodItem) => {
    const categoryMatch = categoryFilter ? foodItem.category === categoryFilter : true;
    const priceMatch = priceFilter ? foodItem.price <= parseInt(priceFilter) : true;
    const searchTermMatch =
      searchTerm
        ? foodItem.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          foodItem.category.toLowerCase().includes(searchTerm.toLowerCase())
        : true;
    return categoryMatch && priceMatch && searchTermMatch;
  });

  const handleCategoryFilterChange = (e) => {
    setCategoryFilter(e.target.value);
  };

  const handlePriceFilterChange = (e) => {
    setPriceFilter(e.target.value);
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleAddToCart = (foodItem, quantity) => {
    const existingItemIndex = cartItems.findIndex((item) => item.foodItem._id === foodItem._id);

    if (existingItemIndex !== -1) {
      const updatedCartItems = [...cartItems];
      updatedCartItems[existingItemIndex].quantity += quantity;
      setCartItems(updatedCartItems);
    } else {
      setCartItems([...cartItems, { foodItem, quantity }]);
    }
  };

  const handleClearCart = () => {
    setCartItems([]);
  };

  const handlePayment = () => {
    // Simulate a successful payment process here.
    // You can add more validation and error handling as needed.

    // For demonstration purposes, we'll just show a successful message after a short delay:
    setTimeout(() => {
      setShowPopup(false);
      setCartItems([]); // Clear the cart after successful payment
      alert('Payment successful! Your order has been placed.');
    }, 1500);
  };

  const calculateTotalBill = () => {
    return cartItems.reduce((total, item) => total + item.foodItem.price * item.quantity, 0);
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredFoodItems.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="bg-gray-800 min-h-screen py-10">
      <div className="container mx-auto px-4 flex flex-wrap">
        <div className="w-full md:w-1/4 lg:w-1/5 px-4 mb-6">
          <div className="bg-white rounded-lg p-4 shadow-md">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Filter Options</h3>
            <div className="mb-4">
              <label htmlFor="categoryFilter" className="block text-md font-medium text-gray-600">
                Category:
              </label>
              <select
                id="categoryFilter"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-200 focus:outline-none"
                value={categoryFilter}
                onChange={handleCategoryFilterChange}
              >
                <option value="">All</option>
                <option value="Vegetarian">Vegetarian</option>
                <option value="Non-Vegetarian">Non-Vegetarian</option>
                <option value="Vegan">Vegan</option>
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="priceFilter" className="block text-md font-medium text-gray-600">
                Price (max):
              </label>
              <input
                type="number"
                id="priceFilter"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-200 focus:outline-none"
                value={priceFilter}
                onChange={handlePriceFilterChange}
              />
            </div>
            <div>
              <label htmlFor="searchBar" className="block text-md font-medium text-gray-600">
                Search:
              </label>
              <input
                type="text"
                id="searchBar"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-200 focus:outline-none"
                value={searchTerm}
                onChange={handleSearchTermChange}
                placeholder="Search by name or category..."
              />
            </div>
          </div>
        </div>
        <div className="w-full md:w-3/4 lg:w-4/5 px-4">
          <div className="flex justify-between">
            <h1 className="text-4xl font-bold text-white mb-8">Food Ordering System</h1>
            {cartItems.length > 0 && (
              <button
                className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
                onClick={handleClearCart}
              >
                Clear Cart
              </button>
            )}
          </div>
          <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
            {currentItems.map((foodItem) => (
              <div key={foodItem._id} className="bg-white rounded-lg p-4 shadow-md cursor-pointer">
                <img
                  src={foodItem.photo}
                  alt={foodItem.name}
                  className="w-full h-32 object-cover rounded-md mb-4"
                />
                <h3 className="text-xl font-semibold text-gray-800">{foodItem.name}</h3>
                <p className="text-lg font-medium text-gray-600 mb-2">Price: ${foodItem.price}</p>
                <p className="text-md font-medium text-gray-600 mb-2">
                  Available: {foodItem.available ? 'Yes' : 'No'}
                </p>
                <p className="text-md font-medium text-gray-600 mb-2">Category: {foodItem.category}</p>
                <FoodItemModal foodItem={foodItem} onAddToCart={handleAddToCart} cartItems={cartItems} />
              </div>
            ))}
          </div>
          <nav className="flex justify-center mt-4">
            <ul className="pagination flex flex-row">
              {Array.from({ length: Math.ceil(filteredFoodItems.length / itemsPerPage) }).map(
                (_, index) => (
                  <li key={index} className="page-item">
                    <button
                      className={`page-link bg-gray-800 text-white px-3 py-1 rounded-md mx-1 mb-2 hover:bg-indigo-500 ${
                        currentPage === index + 1 ? 'bg-indigo-500' : ''
                      }`}
                      onClick={() => paginate(index + 1)}
                    >
                      {index + 1}
                    </button>
                  </li>
                )
              )}
            </ul>
          </nav>
        </div>
        {showPopup && (
          <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
            <div className="bg-white p-4 rounded-lg w-96 relative">
              <div className="flex justify-end mb-2">
                <button className="text-gray-500 hover:text-gray-600" onClick={() => setShowPopup(false)}>
                  <FaTimes size={20} />
                </button>
              </div>
              <h3 className="text-xl font-semibold text-gray-800 mb-2">Checkout</h3>
              {cartItems.map((item) => (
                <div key={item.foodItem._id} className="flex items-center justify-between mb-2">
                  <span className="text-md font-medium text-gray-800">{item.foodItem.name}</span>
                  <span className="text-md font-medium text-gray-800">{item.quantity}</span>
                </div>
              ))}
              <div className="border-t pt-2 mt-2">
                <p className="text-xl font-semibold text-gray-800 mb-2">Total: ${calculateTotalBill()}</p>

                <form>
                  <div className="mb-4">
                    <label htmlFor="cardNumber" className="block text-md font-medium text-gray-600">
                      Card Number:
                    </label>
                    <input
                      type="text"
                      id="cardNumber"
                      className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-200 focus:outline-none"
                      placeholder="Enter card number"
                    />
                  </div>
                  {/* Add other card details fields like expiry date, CVV, etc. */}

                  <div className="mb-4">
                    <label htmlFor="deliveryAddress" className="block text-md font-medium text-gray-600">
                      Delivery Address:
                    </label>
                    <input
                      type="text"
                      id="deliveryAddress"
                      className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-indigo-200 focus:outline-none"
                      placeholder="Enter delivery address"
                    />
                  </div>
                  {/* Add other delivery details fields like name, phone, etc. */}
                </form>

                <div className="flex justify-between">
                  <button
                    className="bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600"
                    onClick={handlePayment}
                  >
                    Pay Now
                  </button>
                  <button
                    className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
                    onClick={handleClearCart}
                  >
                    Clear Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Services;
