import React, { useContext, useEffect ,useState} from 'react'
import { useNavigate } from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Sidebar from '../dashbord/Sidebar';
import Packages from '../dashbord/Packages';
import Profile from '../dashbord/Profile';



const Dashboard = () => {
  const { logindata, setLoginData } = useContext(LoginContext);
  const [data, setData] = useState(false);
  const history = useNavigate();
  const [currentPage, setCurrentPage] = useState('page1');

  const DashboardValid = async () => {
    let token = localStorage.getItem("usersdatatoken");

    const res = await fetch("/validuser", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: token,
      },
    });

    const data = await res.json();

    if (data.status == 401 || !data) {
      history("*");
    } else {
      console.log("user verify");
      setLoginData(data);
      history("/dash");
    }
  };

  useEffect(() => {
    setTimeout(() => {
      DashboardValid();
      setData(true);
    }, 2000);
  }, []);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  }

  return (
    <div className="bg-gray-100 min-h-screen">
      <div className="flex flex-row">
        <Sidebar onPageChange={handlePageChange} />
        <div className="flex-grow bg-gray-100 p-20">
          {data ? (
            <div className="w-full">
              {currentPage === 'Packages' && <Packages logindata={logindata} />}
              {currentPage === 'Profile' && <Profile logindata={logindata} />}
            </div>
          ) : (
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "100vh",
              }}
            >
              Loading... &nbsp;
              <CircularProgress />
            </Box>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
