import React, { useState, useEffect } from 'react';

function PaymentPopup({ pkg, onClose }) {
  const [paymentAmount, setPaymentAmount] = useState(0);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  useEffect(() => {
    const calculatePayment = () => {
      // Perform calculations and update paymentAmount, startDate, and endDate states
      const currentDate = new Date();
      const purchaseDate = new Date(pkg.purchaseDate);
      const validDuration = 30 * 24 * 60 * 60 * 1000; // One month validity in milliseconds

      const isValid = currentDate - purchaseDate <= validDuration;
      setPaymentAmount(isValid ? pkg.price : 0);

      const formattedStartDate = purchaseDate.toLocaleDateString('en-US');
      setStartDate(formattedStartDate);

      const endDateTimestamp = purchaseDate.getTime() + validDuration;
      const formattedEndDate = new Date(endDateTimestamp).toLocaleDateString('en-US');
      setEndDate(formattedEndDate);
    };

    calculatePayment();
  }, [pkg]);

  const handleSubscribe = async () => {
    // Perform the API call to subscribe to the package and save the subscription data
    try {
      const response = await fetch('/api/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ packageId: pkg._id }),
      });

      const data = await response.json();
      console.log(data);

      // Close the payment pop-up box
      onClose();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="absolute inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75">
      <div className="bg-white px-20 py-20 rounded-lg">
        <h3 className="font-bold">Package: {pkg.name}</h3>
        <p className="mt-2 text-gray-600">Price: ${pkg.price}</p>
        <p className="mt-2 text-gray-600">Payment Amount: ${paymentAmount}</p>
        <p className="mt-2 text-gray-600">Start Date: {startDate}</p>
        <p className="mt-2 text-gray-600">End Date: {endDate}</p>
        <div className="flex justify-center mt-4">
          <button
            className="mr-2 px-3 py-1 rounded-lg bg-red-600 text-white"
            onClick={handleSubscribe}
            title="Subscribe"
          >
            Subscribe
          </button>
          <button
            className="mr-2 px-3 py-1 rounded-lg bg-gray-600 text-white"
            onClick={onClose}
            title="Cancel"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}

export default PaymentPopup;
