import React, { useState } from 'react';



function Adduserdata() {


    //add user POST user data 


    const [formData, setFormData] = useState({
        fullName: '',
        address: '',
        dob: '',
        nic: '',
        telephone: '',
        weight: '',
        height: ''
    });

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value,
        });
    };



    const handleSubmit = async (event) => {
        event.preventDefault();
        const token = localStorage.getItem('usersdatatoken');

        const response = await fetch('/user/profile', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: token,
            },
            body: JSON.stringify(formData),
        });

        const data = await response.json();
        console.log(data);

        // Check if the data was saved successfully
        if (data.success) {
            
            setMessage('Data saved successfully');

            // Clear the input fields
            setFormData({
                fullName: '',
                address: '',
                dob: '',
                nic: '',
                telephone: '',
                weight: '',
                height: ''
            });
        }
    };


    return (
        <>
            <h1 className='text-2xl font-bold mb-4 '>Add Your Data </h1>

            <form onSubmit={handleSubmit}>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='fullName'
                        placeholder='Full Name'
                        value={formData.fullName}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='address'
                        placeholder='Address'
                        value={formData.address}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='date'
                        name='dob'
                        placeholder='Date of Birth'
                        value={formData.dob}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='nic'
                        placeholder='National Identification Card'
                        value={formData.nic}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='telephone'
                        placeholder='Telephone'
                        value={formData.telephone}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='number'
                        name='weight'
                        placeholder='Weight'
                        value={formData.weight}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='number'
                        name='height'
                        placeholder='Height'
                        value={formData.height}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='flex justify-start'>
                    <button type='submit' className='bg-blue-500 text-white font-bold py-2 px-4 rounded mr-2'>Save</button>

                </div>
            </form>
          {message && (
                <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative" role="alert">
                    <strong className="font-bold">{message}</strong>
                </div>
            )}
        </>
    );
}

export default Adduserdata;
