import React, { useState, useEffect } from 'react';
import { FaTrashAlt, FaEdit } from 'react-icons/fa';

function PackageList() {
  const [packages, setPackages] = useState([]);
  const [activeCard, setActiveCard] = useState(null);

  // Fetch packages data from the backend
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await fetch('/api/packages');
        const data = await response.json();
        setPackages(data.packages);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPackages();
  }, []);

  const handleCardClick = (pkgId) => {
    setActiveCard(pkgId);
  };

  return (
    <div className="h-327px">
      <div className="container">
        <div className="grid grid-cols-3 gap-4 mt-2">
          {packages.map((pkg) => (
            <div
              key={pkg._id}
              className={`bg-gray-200 p-4 rounded-md ${
                activeCard === pkg._id ? 'active' : ''
              }`}
              onClick={() => handleCardClick(pkg._id)}
            >
              <h3 className="font-bold">{pkg.name}</h3>
              <p className="mt-2 text-gray-600">${pkg.price}</p>
              <p className="mt-2 text-gray-600">{pkg.description}</p>
              <div className="flex justify-end mt-4">
                <button
                  className="mr-2 px-3 py-1 rounded-lg bg-red-600 text-white"
                  title="Delete"
                >
                  buy
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PackageList;
