import React, { useState } from "react";
import { loadStripe } from "@stripe/stripe-js";

const stripePromise = loadStripe("pk_test_51OviJeSCkJx3glD9ied9hu2ePHsjoU9Mt6FNwEHH1mv5OcKneWEFqNqZYHioWCwiSCZxnHBvi98qm01TYjPYotAr00ALHdsty4");

const BookAppointment = () => {
  const [step, setStep] = useState(1);
  const [appointmentData, setAppointmentData] = useState({
    date: "",
    location: "",
    description: ""
  });
  const [paymentData, setPaymentData] = useState({
    amount: "",
    token: null
  });

  const handleAppointmentSubmit = () => {
    setStep(2);
  };

  return (
    <div className="bg-indigo-100 min-h-screen flex rounded-lg">
      <div className="p-4">
        {step === 1 && (
          <div className="bg-gray-100 p-4 rounded-md">
            <label className="block mb-2">Date</label>
            <input
              type="date"
              value={appointmentData.date}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, date: e.target.value })
              }
              className="border border-gray-300 p-2 mb-4 rounded-md w-full"
            />
            <label className="block mb-2">Location</label>
            <input
              type="text"
              value={appointmentData.location}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, location: e.target.value })
              }
              className="border border-gray-300 p-2 mb-4 rounded-md w-full"
            />
            <label className="block mb-2">Description</label>
            <input
              type="text"
              value={appointmentData.description}
              onChange={(e) =>
                setAppointmentData({ ...appointmentData, description: e.target.value })
              }
              className="border border-gray-300 p-2 mb-4 rounded-md w-full"
            />
            <button onClick={handleAppointmentSubmit} className="bg-blue-500 text-white px-4 py-2 rounded-md">
              Next
            </button>
          </div>
        )}
        {step === 2 && (
          <div className="bg-gray-100 p-4 rounded-md">
            <label className="block mb-2">Amount</label>
            <input
              type="number"
              value={paymentData.amount}
              onChange={(e) =>
                setPaymentData({ ...paymentData, amount: e.target.value })
              }
              className="border border-gray-300 p-2 mb-4 rounded-md w-full"
            />
            <button
              onClick={async () => {
                const stripe = await stripePromise;
                const { error } = await stripe.redirectToCheckout({
                  sessionId: paymentData.sessionId
                });
                if (error) {
                  console.error("Error:", error);
                }
              }}
              className="bg-blue-500 text-white px-4 py-2 rounded-md"
              role="link"
            >
              Pay
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookAppointment;
