import React, { useState, useEffect } from "react";

const UserDetail = () => {
  const [userData, setUserData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
         const response = await fetch(`/user/profile`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: localStorage.getItem('usersdatatoken'),
          },
        });

        if (!response.ok) {
          throw new Error(response.statusText);
        }
        const data = await response.json();
        setUserData(data.data);
        setIsLoading(false);
      } catch (error) {
        setError(error);
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  if (isLoading) {
    return <div>Loading user data...</div>;
  }

  if (error) {
    return <div>Error loading user data: {error.message}</div>;
  }

  return (
    <>
     

      <div className='bg-white p-4 rounded-md'>
      <h1 className='text-2xl font-bold mb-4 bg-gradient-to-r from-fuchsia-500 to-cyan-500 bg-clip-text text-transparent pl-1 mr-7 rounded-lg'>
        User Details
      </h1>
        <div className='mb-4'>
          <label className='font-bold'>Full Name:</label>
          <span>{userData.fullName}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Address:</label>
          <span>{userData.address}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Date of Birth:</label>
          <span>{userData.dob}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>National Identification Card:</label>
          <span>{userData.nic}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Telephone:</label>
          <span>{userData.telephone}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Weight:</label>
          <span>{userData.weight}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Height:</label>
          <span>{userData.height}</span>
        </div>
      </div>
    </>
  );
};

export default UserDetail;
