import React, { useState } from 'react';
import { FaUser, FaTimes, FaUserPlus, FaUserEdit } from 'react-icons/fa';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

//import Adduserdata from '../dashbord/profileitems/Adduserdata';


function Profile() {



  //add user POST user data 
  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    dob: '',
    nic: '',
    telephone: '',
    weight: '',
    height: ''
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const token = localStorage.getItem('usersdatatoken');

    const response = await fetch('/user/profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(formData),
    });

    const data = await response.json();
    console.log(data);
    if (res.status === 201) {
      toast.success('Data added successfully');
    }



    // Check if the data was saved successfully
    if (data.success) {
      // Clear the input fields
      setFormData({
        fullName: '',
        address: '',
        dob: '',
        nic: '',
        telephone: '',
        weight: '',
        height: ''
      });
      
    }
  };

  //calender
  const [date, setDate] = useState(new Date());
  const [showPopup, setShowPopup] = useState(false);

  const handleDateChange = (date) => {
    setDate(date);
  };

  const handlePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className='bg-gradient-to-r from-indigo-300 to-purple-400 flex rounded-lg'>
      {/* First Part */}
      <div className='w-3/4 rounded-l-lg  p-2 min-h-screen'>
        <div className='flex justify-center items-center mb-8 '>
          <FaUser className='text-4xl mr-4' />
          <h1 className='text-4xl font-bold'>Profile Details</h1>
        </div>

        <div>

        </div>

      </div>
      {/* Second Part */}
      <div className='w-1/4 rounded-r-lg bg-gradient-to-b from-gray-900 via-purple-900 to-violet-600 p-8'>
        <div className='flex flex-col justify-center items-center'>
          <Calendar value={date} onChange={handleDateChange} />
          <button className='bg-gradient-to-r from-rose-400 via-fuchsia-500 to-indigo-500 hover:bg-gradient-to-r hover:from-fuchsia-500 hover:to-indigo-500 text-white font-bold py-2 px-5 rounded focus:outline-none focus:shadow-outline inline-flex items-center mt-4 w-full justify-center ' onClick={handlePopup}>  <FaUserPlus className='mr-2' />Add Data</button>
          {showPopup && (
            <div className='fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center'>
              <div className='bg-gradient-to-br from-gray-900 via-purple-900 to-violet-600 w-3/4 h-4/5 rounded-lg p-8 relative'>
                <button className='bg-gradient-to-r from-rose-400 to-red-500 text-white font-bold py-2 px-4 rounded absolute top-0 right-0 mt-2 mr-2' onClick={handleClosePopup}><FaTimes /></button>
                <>
                  <h1 className='text-2xl font-bold mb-4 bg-gradient-to-r from-fuchsia-500 to-cyan-500 bg-clip-text text-transparent pl-3 mr-7 rounded-lg  '>Add your Information</h1>

                  <form onSubmit={handleSubmit} className='bg-white p-4  rounded-md' >
                    <div className='mb-4'>
                      <input
                        type='text'
                        name='fullName'
                        placeholder='Full Name'
                        value={formData.fullName}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='text'
                        name='address'
                        placeholder='Address'
                        value={formData.address}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='date'
                        name='dob'
                        placeholder='Date of Birth'
                        value={formData.dob}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='text'
                        name='nic'
                        placeholder='National Identification Card'
                        value={formData.nic}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='text'
                        name='telephone'
                        placeholder='Telephone'
                        value={formData.telephone}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='number'
                        name='weight'
                        placeholder='Weight'
                        value={formData.weight}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                      />
                    </div>
                    <div className='mb-4'>
                      <input
                        type='number'
                        name='height'
                        placeholder='Height'
                        value={formData.height}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'

                      />
                    </div>
                    <div className='flex justify-start'>
                      <button type='submit' className='bg-gradient-to-r from-fuchsia-600 to-pink-600 text-white font-bold py-2 px-4 rounded mr-2'>Save</button>

                    </div>

                  </form>

                </>

              </div>
            </div>

          )}
          <button className='bg-gradient-to-r from-emerald-400 to-cyan-400 hover:bg-gradient-to-r hover:from-cyan-400 hover:to-emerald-400  text-white font-bold py-2 px-5 rounded focus:outline-none focus:shadow-outline inline-flex items-center w-full justify-center mt-4'><FaUserEdit className='mr-2' />Update Data</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
