import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('YOUR_STRIPE_PUBLISHABLE_KEY');

function Packages() {
  const [selectedPackage, setSelectedPackage] = useState(null);

  const handleBuyClick = (packageInfo) => {
    setSelectedPackage(packageInfo);
  };

  const handlePayment = async () => {
    try {
      const stripe = await stripePromise;
      const response = await fetch('/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ package: selectedPackage.package, amount: selectedPackage.amount })
      });
      const session = await response.json();

      // Redirect to the Stripe checkout page
      const result = await stripe.redirectToCheckout({
        sessionId: session.id
      });

      if (result.error) {
        // Handle any error that occurs during the redirect to Stripe checkout
        throw new Error(result.error.message);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="bg-white min-h-screen p-4">
      <div className="flex justify-center">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {/* Package cards */}
        </div>
      </div>

      {/* Popup box */}
      {selectedPackage && (
        <div className="fixed top-0 left-0 right-0 bottom-0 flex items-center justify-center bg-gray-500 bg-opacity-50">
          <div className="bg-white p-4 rounded-xl">
            <h2 className="text-2xl font-bold mb-4">
              {selectedPackage.package}
            </h2>
            <p className="text-lg mb-4">Amount: ${selectedPackage.amount}</p>
            <button
              className="bg-indigo-500 text-white px-4 py-2 rounded"
              onClick={handlePayment}
            >
              Pay
            </button>
            <button
              className="bg-red-500 text-white px-4 py-2 rounded ml-2"
              onClick={() => setSelectedPackage(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Packages;
