import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Register = () => {
  const [currentStep, setCurrentStep] = useState(1); // Manage form steps
  

  const handleRegistrationSubmit = async (e) => {
    e.preventDefault();
    // Implement registration logic here
    // If registration is successful, move to next step
    setCurrentStep(2);
  };

  const handleDetailsSubmit = async (e) => {
    e.preventDefault();
    // Implement details submission logic here, including photo upload
  };

  return (
    <>
      {currentStep === 1 && (
        <form onSubmit={handleRegistrationSubmit} className='bg=blue-500'>
         
          <button type="submit" onClick={handleRegistrationSubmit}>Next</button>
        </form>
      )}
      {currentStep === 2 && (
        <form onSubmit={handleDetailsSubmit} className='bg-red-200'>
          
          <button type="submit">Submit Details</button>
        </form>
      )}
      <ToastContainer />
    </>
  );
};

export default Register;
