import React, { useState, useEffect } from 'react';
import { FaCartPlus, FaTimes } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import FoodItemModal from '../../dashbord/food/FoodItem';

const Services = () => {
  const [jobs, setJobs] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceFilter, setPriceFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    // Fetching job data, replace this with your own data fetching logic
    const jobData = [
      { id: 1, title: 'Job 1', category: 'Category A', price: 100 },
      { id: 2, title: 'Job 2', category: 'Category B', price: 200 },
      // Add more job data here
    ];
    setJobs(jobData);
  };

  const filteredJobs = jobs.filter((job) => {
    const categoryMatch = categoryFilter ? job.category === categoryFilter : true;
    const priceMatch = priceFilter ? job.price <= parseInt(priceFilter) : true;
    const searchTermMatch =
      searchTerm
        ? job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          job.category.toLowerCase().includes(searchTerm.toLowerCase())
        : true;
    return categoryMatch && priceMatch && searchTermMatch;
  });

  const handleCategoryFilterChange = (e) => {
    setCategoryFilter(e.target.value);
  };

  const handlePriceFilterChange = (e) => {
    setPriceFilter(e.target.value);
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentJobs = filteredJobs.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="bg-blue-300 min-h-screen py-10">
      <div className="container mx-auto px-4 flex flex-wrap">
        <div className="w-full md:w-1/4 lg:w-1/5 px-4 mb-6">
          <div className="bg-white rounded-lg p-4 shadow-md">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Filter Options</h3>
            <div className="mb-4">
              <label htmlFor="categoryFilter" className="block text-md font-medium text-gray-600">
                Category:
              </label>
              <select
                id="categoryFilter"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-blue-200 focus:outline-none"
                value={categoryFilter}
                onChange={handleCategoryFilterChange}
              >
                <option value="">All</option>
                <option value="Category A">Category A</option>
                <option value="Category B">Category B</option>
                {/* Add more categories if needed */}
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="priceFilter" className="block text-md font-medium text-gray-600">
                Price:
              </label>
              <input
                type="number"
                id="priceFilter"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-blue-200 focus:outline-none"
                value={priceFilter}
                onChange={handlePriceFilterChange}
              />
            </div>
            <div>
              <label htmlFor="searchBar" className="block text-md font-medium text-gray-600">
                Search:
              </label>
              <input
                type="text"
                id="searchBar"
                className="block w-full px-4 py-2 border rounded-md shadow-sm focus:ring focus:ring-blue-200 focus:outline-none"
                value={searchTerm}
                onChange={handleSearchTermChange}
                placeholder="Search by title or category..."
              />
            </div>
          </div>
        </div>
        <div className="w-full md:w-3/4 lg:w-4/5 px-4">
          <div className='flex justify-between'>
            <h1 className="text-4xl font-bold text-black mb-8">Job Listings</h1>
          </div>
          <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
            {currentJobs.map((job) => (
              <div
                key={job.id}
                className="bg-white rounded-lg p-4 shadow-md cursor-pointer"
              >
                <h3 className="text-xl font-semibold text-gray-800">{job.title}</h3>
                <p className="text-lg font-medium text-gray-600 mb-2">Price: ${job.price}</p>
                <p className="text-md font-medium text-gray-600 mb-2">Category: {job.category}</p>
              </div>
            ))}
          </div>
          <nav className="flex justify-center mt-4 bg-blue-300">
            <ul className="pagination flex flex-row">
              {Array.from({ length: Math.ceil(filteredJobs.length / itemsPerPage) }).map((_, index) => (
                <li key={index} className="page-item">
                  <button
                    className={`page-link bg-blue-800 text-white px-3 py-1 rounded-md mx-1 mb-2 hover:bg-blue-600 ${
                      currentPage === index + 1 ? 'bg-blue-600' : ''
                    }`}
                    onClick={() => paginate(index + 1)}
                  >
                    {index + 1}
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default Services;
