import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
} from "@chakra-ui/react";
import { ChakraProvider } from "@chakra-ui/react";
import { useNavigate} from "react-router-dom"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./mix.css"

const Register = () => {
  const [formData, setFormData] = useState({
    fname: "",
    email: "",
    password: "",
    cpassword: ""
  });

  const history = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8009/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          ...formData,
          role: "user" // Set default role to "user"
        })
      });

      if (!response.ok) {
        throw new Error("Failed to register");
      }

      const data = await response.json();
      console.log(data); // Log the response from the server
      // You can perform further actions here, such as redirecting the user or showing a success message
    } catch (error) {
      console.error(error); // Log any errors
      // Handle errors here, such as displaying an error message to the user
    }
  };

  return (
    <ChakraProvider>

     <div className="form-background">
      <Box maxW="md" mx="auto" p="6">
        <div className="m-30 bg-gray-100 rounded-xl">

          
      <Box p={4}>
        <form onSubmit={handleSubmit}>
          <FormControl id="fname" isRequired>
            <FormLabel>Full Name</FormLabel>
            <Input
              type="text"
              name="fname"
              value={formData.fname}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl id="email" isRequired mt={4}>
            <FormLabel>Email</FormLabel>
            <Input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl id="password" isRequired mt={4}>
            <FormLabel>Password</FormLabel>
            <Input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl id="cpassword" isRequired mt={4}>
            <FormLabel>Confirm Password</FormLabel>
            <Input
              type="password"
              name="cpassword"
              value={formData.cpassword}
              onChange={handleChange}
            />
          </FormControl>
          <Button type="submit" colorScheme="teal" mt={4}>
            Register
          </Button>
        </form>
      </Box>

      </div>  
     </Box>
   </div>   

    </ChakraProvider>
  );
};

export default Register;
