import React, { useState } from 'react'
import { NavLink, useNavigate } from "react-router-dom"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import "./mix.css"

const Login = () => {

    const [passShow, setPassShow] = useState(false);
    const [inpval, setInpval] = useState({ email: "", password: "" });
    const history = useNavigate();

    const setVal = (e) => {
        const { name, value } = e.target;

        setInpval(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const loginuser = async (e) => {
        e.preventDefault();
        const { email, password } = inpval;

        if (email === "" || !email.includes("@")) {
            toast.error("Invalid email!", { position: "top-center" });
            return;
        }

        if (password === "" || password.length < 6) {
            toast.error("Password must be at least 6 characters long!", { position: "top-center" });
            return;
        }

        try {
            const data = await fetch("/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email, password })
            });

            const res = await data.json();

            if (res.status === 201) {
                localStorage.setItem("usersdatatoken", res.result.token);
                history("/dash");
                setInpval({ email: "", password: "" });
                toast.success("Login successful!", { position: "top-center" });
            } else {
                toast.error("Login failed. Please check your credentials!", { position: "top-center" });
            }
        } catch (error) {
            console.error(error);
            toast.error("Something went wrong. Please try again later!", { position: "top-center" });
        }
    }

    return (
        <>
            <section className='form-background'>
                <div className="form_data">
                    <div className="form_heading">
                        <h1>Welcome Back, Log In</h1>
                        <p>Hi, we are glad you are back. Please login.</p>
                    </div>

                    <form>
                        <div className="form_input">
                            <label htmlFor="email">Email</label>
                            <input type="email" value={inpval.email} onChange={setVal} name="email" id="email" placeholder='Enter Your Email Address' />
                        </div>
                        <div className="form_input">
                            <label htmlFor="password">Password</label>
                            <div className="two">
                                <input type={!passShow ? "password" : "text"} onChange={setVal} value={inpval.password} name="password" id="password" placeholder='Enter Your password' />
                                <div className="showpass" onClick={() => setPassShow(!passShow)}>
                                    {!passShow ? "Show" : "Hide"}
                                </div>
                            </div>
                        </div>

                        <button className='btn' onClick={loginuser}>Login</button>
                        <div className="flex justify-between">
                            <p> <NavLink to="/forget-password">Forget Password ?</NavLink> </p>
                            <p>Don't have an Account? <NavLink to="/register">Sign Up</NavLink> </p>
                        </div>
                    </form>
                    <ToastContainer />
                </div>
            </section>
        </>
    )
}

export default Login
