import React, { useState } from 'react';
import { FaTimes, FaCartPlus } from 'react-icons/fa';

const FoodItemModal = ({ foodItem, onAddToCart, onClose }) => {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    onAddToCart(foodItem, quantity);
    onClose(); // Close the modal after adding to cart.
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-4 rounded-lg w-96 relative">
        {/* ... (existing code) */}

        <div className="flex items-center">
          <label htmlFor="quantity" className="mr-2">
            Quantity:
          </label>
          <input
            type="number"
            id="quantity"
            className="border rounded-md px-2 py-1"
            min="1"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />

          {/* "Add to Cart" button with the FaCartPlus icon */}
          <button
            className="ml-4 bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600"
            onClick={handleAddToCart}
          >
            <FaCartPlus className="mr-2" />
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default FoodItemModal;
