import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function UserDetail(props) {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const { email } = useParams();

  useEffect(() => {
    async function getUserData() {
      try {
        const response = await fetch(`/user/profile/${email}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: localStorage.getItem('usersdatatoken'),
          },
        });

        const data = await response.json();
        if (response.status === 200) {
          setUserData(data);
        } else {
          toast.error('Error retrieving user data', {
            position: toast.POSITION.TOP_CENTER,
            toastStyle: { width: '300px' },
            autoClose: 500,
            style: {
              background: '#FFFFFF',
              color: '#000000',
            },
          });
        }
      } catch (error) {
        console.error(error);
        toast.error('Error retrieving user data', {
          position: toast.POSITION.TOP_CENTER,
          toastStyle: { width: '300px' },
          autoClose: 500,
          style: {
            background: '#FFFFFF',
            color: '#000000',
          },
        });
      } finally {
        setLoading(false);
      }
    }
    getUserData();
  }, [email]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!userData) {
    return (
      <div>
        <h1>User Not Found</h1>
      </div>
    );
  }

  return (
    <>
     

      <div className='bg-white p-4 rounded-md'>
      <h1 className='text-2xl font-bold mb-4 bg-gradient-to-r from-fuchsia-500 to-cyan-500 bg-clip-text text-transparent pl-1 mr-7 rounded-lg'>
        User Details
      </h1>
        <div className='mb-4'>
          <label className='font-bold'>Full Name:</label>
          <span>{userData.fullName}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Address:</label>
          <span>{userData.address}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Date of Birth:</label>
          <span>{userData.dob}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>National Identification Card:</label>
          <span>{userData.nic}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Telephone:</label>
          <span>{userData.telephone}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Weight:</label>
          <span>{userData.weight}</span>
        </div>
        <div className='mb-4'>
          <label className='font-bold'>Height:</label>
          <span>{userData.height}</span>
        </div>
      </div>
    </>
  );
}

export default UserDetail;
