import React, { useState } from 'react';



function Adduserdata() {

    const [dataSaved, setDataSaved] = useState(false);



    //add user POST user data 
    const [formData, setFormData] = useState({
        fullName: '',
        address: '',
        dob: '',
        nic: '',
        telephone: '',
        weight: '',
        height: ''
    });

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const token = localStorage.getItem('usersdatatoken');

        const response = await fetch('/user/profile', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: token,
            },
            body: JSON.stringify(formData),
        });

        const data = await response.json();
        console.log(data);

        // Check if the data was saved successfully
        if (data.success) {
            // Clear the input fields
            setFormData({
                fullName: '',
                address: '',
                dob: '',
                nic: '',
                telephone: '',
                weight: '',
                height: ''
            });

            // Set the dataSaved state variable to true
            setDataSaved(true);

            // Reset the dataSaved state variable to false after a short delay
            setTimeout(() => {
                setDataSaved(false);
            }, 3000);
        }
    };


    return (
        <>
            <h1 className='text-2xl font-bold mb-4 bg-gradient-to-r from-fuchsia-500 to-cyan-500 bg-clip-text text-transparent pl-3 mr-7 rounded-lg  '>Add your Information</h1>

            <form onSubmit={handleSubmit} className='bg-white p-4  rounded-md' >
                <div className='mb-4'>
                    <input
                        type='text'
                        name='fullName'
                        placeholder='Full Name'
                        value={formData.fullName}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='address'
                        placeholder='Address'
                        value={formData.address}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='date'
                        name='dob'
                        placeholder='Date of Birth'
                        value={formData.dob}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='nic'
                        placeholder='National Identification Card'
                        value={formData.nic}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='text'
                        name='telephone'
                        placeholder='Telephone'
                        value={formData.telephone}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='number'
                        name='weight'
                        placeholder='Weight'
                        value={formData.weight}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'
                    />
                </div>
                <div className='mb-4'>
                    <input
                        type='number'
                        name='height'
                        placeholder='Height'
                        value={formData.height}
                        onChange={handleChange}
                        className='w-full px-3 py-2 border rounded'

                    />
                </div>
                <div className='flex justify-start'>
                    <button type='submit' className='bg-gradient-to-r from-fuchsia-600 to-pink-600 text-white font-bold py-2 px-4 rounded mr-2'>Save</button>

                </div>

            </form>
            {dataSaved && <p>Data saved successfully!</p>}
        </>
    );
}

export default Adduserdata;
