import React, { useContext, useEffect ,useState} from 'react'
import { NavLink,useNavigate } from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Packages from "../dashbord/Packages";
import MyFitness from "../dashbord/MyFitness";
import QRCode from "../dashbord/QRCode";
import Profile from "../dashbord/Profile";

const Dashboard = () => {
    const { logindata, setLoginData } = useContext(LoginContext);
    const [data, setData] = useState(false);
    const history = useNavigate();
  
    const DashboardValid = async () => {
      let token = localStorage.getItem("usersdatatoken");
  
      const res = await fetch("/validuser", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: token,
        },
      });
  
      const data = await res.json();
  
      if (data.status == 401 || !data) {
        history("*");
      } else {
        console.log("user verify");
        setLoginData(data);
        history("/dash");
      }
    };
  
    useEffect(() => {
      setTimeout(() => {
        DashboardValid();
        setData(true);
      }, 2000);
    }, []);
  
    return (
      <div className="flex flex-row h-screen bg-gray-100">
        {/* Sidebar */}
        <div className="bg-white w-60 p-4">
          <h2 className="text-xl font-bold mb-4">Dashboard</h2>
          <nav className="space-y-2 bg-slate-600">
          <NavLink to="/Packages">
           <div className='className="block p-2 rounded-md hover:bg-gray-200"'>
           Components
           </div>

          </NavLink>
            <a
              href="#"
              className="block p-2 rounded-md hover:bg-gray-200"
            >
              Packages
            </a>
            <a
              href="#"
              className="block p-2 rounded-md hover:bg-gray-200"
            >
              My Fitness
            </a>
            <a
              href="#"
              className="block p-2 rounded-md hover:bg-gray-200"
            >
              QR Code
            </a>
            <a
              href="#"
              className="block p-2 rounded-md hover:bg-gray-200"
            >
              Profile
            </a>
          </nav>
        </div>
  
        {/* Content */}
        <div className="flex-grow p-6">
          {data ? (
            <div className="flex flex-col items-center">
              <img src="./man.png" className="w-72 mb-6" alt="man" />
              <h1 className="text-2xl">
                User Email:{" "}
                {logindata ? logindata.ValidUserOne.email : ""}
              </h1>
            </div>
          ) : (
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "100vh",
              }}
            >
              Loading... &nbsp;
              <CircularProgress />
            </Box>
          )}
        </div>
      </div>
    );
  };
  
  export default Dashboard;
