import React, { useState } from 'react';
import { FaUser } from 'react-icons/fa';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

function Profile() {

  //add user details


  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    dob: '',
    nic: '',
    telephone: '',
    weight: '',
    height: ''
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const token = localStorage.getItem('usersdatatoken');

    const response = await fetch('/user/profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: token,
      },
      body: JSON.stringify(formData),
    });

    const data = await response.json();
    console.log(data);
  };

  //calender
  const [date, setDate] = useState(new Date());
  const [showPopup, setShowPopup] = useState(false);

  const handleDateChange = (date) => {
    setDate(date);
  };

  const handlePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className='bg-white flex rounded-lg'>
      {/* First Part */}
      <div className='w-3/4 rounded-l-lg bg-red-500 p-8 min-h-screen'>
        <div className='flex justify-center items-center mb-8'>
          <FaUser className='text-4xl mr-4' />
          <h1 className='text-4xl font-bold'>Profile Details</h1>
        </div>
        <div>
          <p className='text-gray-600 mb-2'>Name: </p>
          <input type='text' className='w-full border border-gray-300 rounded-md p-2 mb-4' />
          <p className='text-gray-600 mb-2'>Address: </p>
          <input type='text' className='w-full border border-gray-300 rounded-md p-2 mb-4' />
          <button className='bg-blue-500 text-white font-bold py-2 px-4 rounded'>Update</button>
        </div>
      </div>
      {/* Second Part */}
      <div className='w-1/4 rounded-r-lg bg-white p-8'>
        <div className='flex flex-col justify-center items-center'>
          <div className='w-24 h-24 rounded-full bg-gray-400 mb-4'></div>
          <Calendar value={date} onChange={handleDateChange} />
          <button className='bg-blue-500 text-white font-bold py-2 px-4 rounded mt-4' onClick={handlePopup}>Add Data</button>
          {showPopup && (
            <div className='fixed inset-0 z-10 overflow-y-auto bg-black bg-opacity-50'>
              <div className='flex items-center justify-center min-h-screen'>
                <div className='bg-yellow-400 rounded-lg p-8 w-10'>
                  <h2 className='text-xl font-bold mb-4'>Add User Details</h2>
                  <form onSubmit={handleSubmit} className="bg-white p-4 rounded-lg shadow-md">
                    <div className="mb-4">
                      <label htmlFor="fullName" className="block text-gray-700 font-bold mb-2">Full Name</label>
                      <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="address" className="block text-gray-700 font-bold mb-2">Address</label>
                      <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="dob" className="block text-gray-700 font-bold mb-2">Date of Birth</label>
                      <input
                        type="date"
                        name="dob"
                        value={formData.dob}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="nic" className="block text-gray-700 font-bold mb-2">National Identification Card</label>
                      <input
                        type="text"
                        name="nic"
                        value={formData.nic}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="telephone" className="block text-gray-700 font-bold mb-2">Telephone</label>
                      <input
                        type="text"
                        name="telephone"
                        value={formData.telephone}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="weight" className="block text-gray-700 font-bold mb-2">Weight</label>
                      <input
                        type="number"
                        name="weight"
                        value={formData.weight}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>
                    <div className="mb-4">
                      <label htmlFor="height" className="block text-gray-700 font-bold mb-2">Height</label>
                      <input
                        type="number"
                        name="height"
                        value={formData.height}
                        onChange={handleChange}
                        className="border rounded-md py-2 px-3 w-full"
                      />
                    </div>

                  </form>
                  <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                    Save
                  </button>
                  <button className='bg-red-500 text-white font-bold py-2 px-4 rounded ml-2' onClick={handleClosePopup}>Close</button>
                </div>
              </div>
            </div>
          )}
          <button className='bg-blue-500 text-white font-bold py-2 px-4 rounded mt-2'>Update Data</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
