import React, { useState } from 'react';
import { FaTimes, FaCartPlus } from 'react-icons/fa';

const FoodItemModal = ({ foodItem, onClose }) => {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    // Here, you can add the selected food item with the given quantity to the cart.
    // You can use a state management library like Redux to manage the cart state globally or pass it down through props to the parent component.
    // For simplicity, we'll just alert the selected item and quantity.
    alert(`Added ${quantity} ${foodItem.name}(s) to cart.`);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-4 rounded-lg w-96 relative">
        <div className="flex justify-end mb-2">
          <button className="text-gray-500 hover:text-gray-600" onClick={onClose}>
            <FaTimes size={20} />
          </button>
        </div>
        <img
          src={foodItem.photo}
          alt={foodItem.name}
          className="w-full h-64 object-cover rounded-md mb-4"
          style={{ objectFit: 'cover', maxHeight: '60%' }}
        />
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{foodItem.name}</h3>
        <p className="text-lg font-medium text-gray-600 mb-2">Price: ${foodItem.price}</p>
        <p className="text-md font-medium text-gray-600 mb-2">
          Available: {foodItem.available ? 'Yes' : 'No'}
        </p>
        <p className="text-md font-medium text-gray-600 mb-2">Category: {foodItem.category}</p>

        <div className="flex items-center">
          <label htmlFor="quantity" className="mr-2">
            Quantity:
          </label>
          <input
            type="number"
            id="quantity"
            className="border rounded-md px-2 py-1"
            min="1"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />

          {/* "Add to Cart" button with the FaCartPlus icon */}
          <button
            className="ml-4 bg-indigo-500 text-white px-4 py-2 rounded-md hover:bg-indigo-600"
            onClick={handleAddToCart}
          >
            <FaCartPlus className="mr-2" />
             </button>
        </div>
      </div>
    </div>
  );
};

export default FoodItemModal;
