import React from 'react';

const CheckoutPage = ({ cartItems }) => {
  const calculateTotalPrice = () => {
    let totalPrice = 0;
    for (const { foodItem, quantity } of cartItems) {
      totalPrice += foodItem.price * quantity;
    }
    return totalPrice;
  };

  return (
    <div className="bg-gray-800 min-h-screen py-10">
      <div className="container mx-auto px-4">
        <h1 className="text-4xl font-bold text-white mb-8">Checkout</h1>
        <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
          {cartItems.map(({ foodItem, quantity }) => (
            <div key={foodItem._id} className="bg-white rounded-lg p-4 shadow-md">
              <img
                src={foodItem.photo}
                alt={foodItem.name}
                className="w-full h-32 object-cover rounded-md mb-4"
              />
              <h3 className="text-xl font-semibold text-gray-800">{foodItem.name}</h3>
              <p className="text-lg font-medium text-gray-600 mb-2">Price: ${foodItem.price}</p>
              <p className="text-md font-medium text-gray-600 mb-2">Quantity: {quantity}</p>
            </div>
          ))}
        </div>
        <div className="mt-8">
          <h2 className="text-2xl font-bold text-white">Total Price: ${calculateTotalPrice()}</h2>
        </div>
      </div>
    </div>
  );
};

export default CheckoutPage;
