import React, { useContext, useEffect ,useState} from 'react'
import { useNavigate,Outlet,Link,Route,Routes   } from 'react-router-dom';
import { LoginContext } from './ContextProvider/Context';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Packages from '../dashbord/Packages';
import Profile from '../dashbord/Profile';


const Dashboard = () => {
    const { logindata, setLoginData } = useContext(LoginContext);
    const [data, setData] = useState(false);
    const history = useNavigate();
  
    const DashboardValid = async () => {
      let token = localStorage.getItem("usersdatatoken");
  
      const res = await fetch("/validuser", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: token,
        },
      });
  
      const data = await res.json();
  
      if (data.status == 401 || !data) {
        history("*");
      } else {
        console.log("user verify");
        setLoginData(data);
        history("/dash");
      }
    };
  
    useEffect(() => {
      setTimeout(() => {
        DashboardValid();
        setData(true);
      }, 2000);
    }, []);
  
    const Sidebar = () => {
      return (
        <div>
          <ul>
            <li>
              <Link to="/dash/page1"> Packages</Link>
            </li>
            <li>
              <Link to="/dash/page2">Profile</Link>
            </li>
          </ul>
        </div>
      );
    };
  
    // const Page1 = () => {
    //   return (
    //     <div >
    //       <h1>Page 1</h1>
    //       <p>This is the content for Page 1 Lorem </p>
    //     </div>
    //   );
    // };
  
    // const Page2 = () => {
    //   return (
    //     <div>
    //       <h1>Page 2</h1>
    //       <p>This is the content for Page 2</p>
    //     </div>
    //   );
    // };
  
    return (
      <div className="bg-gray-100 min-h-screen">
        <div className="flex">
          <div className="w-1/6 bg-gray-800 h-screen">
            <Sidebar />
          </div>
          <div className="w-3/4">
            {data ? (
              <div className="flex flex-col items-center pt-20">
               
                <Routes>
                <Route path="/dash/page1" element={<Packages />} />
                <Route path="/dash/page2" element={<Profile />} />
                </Routes>
                
                
              </div>
            ) : (
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  height: "100vh",
                  
                }}
              >
                Loading... &nbsp;
                <CircularProgress />
              </Box>
            )}
          </div>
        </div>
      </div>
    );
  };
  
  export default Dashboard;
