import React, { useState, useEffect } from 'react';
import { FaTrashAlt, FaEdit } from 'react-icons/fa';

function PackageList() {
  const [packages, setPackages] = useState([]);
  const [slideIndex, setSlideIndex] = useState(0);

  // Fetch packages data from the backend
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await fetch('/api/packages');
        const data = await response.json();
        setPackages(data.packages);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPackages();
  }, []);

  const handleSlideLeft = () => {
    setSlideIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : packages.length - 1));
  };

  const handleSlideRight = () => {
    setSlideIndex((prevIndex) => (prevIndex < packages.length - 1 ? prevIndex + 1 : 0));
  };

  return (
    <div className="h-327px relative ">
      <div className="flex absolute transition-transform ease-in-out duration-300" style={{ transform: `translateX(-${slideIndex * 100}%)` }}>
        {packages.map((pkg) => (
          <div key={pkg._id} className="w-full bg-gray-200 p-4 rounded-md">
            <h3 className="font-bold">{pkg.name}</h3>
            <p className="mt-2 text-gray-600">${pkg.price}</p>
            <p className="mt-2 text-gray-600">{pkg.description}</p>
            <div className="flex justify-end mt-4">
              <button className="mr-2 px-3 py-1 rounded-lg bg-red-600 text-white" title="Delete">
                buy
              </button>
              
            </div>
          </div>
        ))}
      </div>
      <button className="absolute top-1/2 left-2 transform -translate-y-1/2 text-2xl text-gray-600" onClick={handleSlideLeft}>
        &lt;
      </button>
      <button className="absolute top-1/2 right-2 transform -translate-y-1/2 text-2xl text-gray-600" onClick={handleSlideRight}>
        &gt;
      </button>
    </div>
  );
}

export default PackageList;
