import React from 'react';

const Checkout = ({ cartItems }) => {
  // Calculate the total price of items in the cart
  const totalAmount = cartItems.reduce((total, item) => total + item.foodItem.price * item.quantity, 0);

  return (
    <div className="bg-gray-800 min-h-screen py-10">
      <div className="container mx-auto px-4">
        <h1 className="text-4xl font-bold text-white mb-8">Checkout</h1>
        <div className="bg-white rounded-lg p-4 shadow-md">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">Your Cart</h3>
          {cartItems.map((cartItem) => (
            <div key={cartItem.foodItem._id} className="flex justify-between mb-2">
              <p>{cartItem.foodItem.name}</p>
              <p>Quantity: {cartItem.quantity}</p>
              <p>Price: ${cartItem.foodItem.price * cartItem.quantity}</p>
            </div>
          ))}
          <hr className="my-4" />
          <div className="flex justify-end">
            <h4 className="text-lg font-medium text-gray-600">Total: ${totalAmount}</h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
