import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Profile() {
  const [userData, setUserData] = useState({});
  const [updateData, setUpdateData] = useState({
    // set initial values of update form data
  });

  useEffect(() => {
    // fetch user data from server
    axios.get('/api/user/profile', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
    })
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const handleUpdateDataChange = event => {
    setUpdateData({
      ...updateData,
      [event.target.name]: event.target.value,
    });
  };

  const handleUpdateDataSubmit = event => {
    event.preventDefault();
    // send update data to server
    axios.put('/api/user/profile', updateData, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
    })
      .then(response => {
        setUserData(response.data);
        setUpdateData({
          // reset update form data
        });
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div className='bg-gray-300 min-h-screen'>
      <h1>Profile</h1>
      <div>
        <h2>User Data:</h2>
        <p>Name: {userData.name}</p>
        <p>Age: {userData.age}</p>
        <p>Gender: {userData.gender}</p>
      </div>
      <form onSubmit={handleUpdateDataSubmit}>
        {/* add input fields for update data */}
        <button type='submit'>Update</button>
      </form>
    </div>
  );
}

export default Profile;
