import React, { useState } from 'react';

function Profile() {
  const [profileData, setProfileData] = useState({
    bio: '',
    location: '',
    skills: '',
    // add more fields as per your requirement
  });

  const handleProfileDataChange = event => {
    setProfileData({
      ...profileData,
      [event.target.name]: event.target.value,
    });
  };

  const handleAddProfile = async event => {
    event.preventDefault();

    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No authentication token found');
      return;
    }

    try {
      const response = await fetch('/api/user/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(profileData),
      });

      if (response.ok) {
        console.log('Profile created successfully');
        // clear the form data
        setProfileData({
          bio: '',
          location: '',
          skills: '',
        });
      } else {
        console.error(`Failed to create profile: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error(`Error creating profile: ${error.message}`);
    }
  };

  return (
    <div className='bg-gray-300 min-h-screen'>
      <h1>Profile</h1>
      <form onSubmit={handleAddProfile}>
        <div>
          <label htmlFor='bio'>Bio:</label>
          <textarea id='bio' name='bio' value={profileData.bio} onChange={handleProfileDataChange} />
        </div>
        <div>
          <label htmlFor='location'>Location:</label>
          <input type='text' id='location' name='location' value={profileData.location} onChange={handleProfileDataChange} />
        </div>
        <div>
          <label htmlFor='skills'>Skills:</label>
          <input type='text' id='skills' name='skills' value={profileData.skills} onChange={handleProfileDataChange} />
        </div>
        <button type='submit'>Add Profile</button>
      </form>
    </div>
  );
}

export default Profile;
